CREATE OR REPLACE PROCEDURE enviar_documento_local_externo ( nr_seq_doc_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
