elsif (ie_opcao_p = 'DE') then 
	update	gestao_documento 
	set	dt_envio 		 = NULL, 
		nm_usuario_envio 	 = NULL, 
		dt_atualizacao		= clock_timestamp() 
	where	nr_sequencia 		= nr_seq_doc_p;
end if;
 
commit;
 
end;
