CREATE OR REPLACE PROCEDURE enviar_email_autor_cirurgia ( nr_seq_autorizacao_p bigint, ds_email_destino_p text, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_autorizacao_p IS NOT NULL AND nr_seq_autorizacao_p::text <> '') and (ds_email_destino_p IS NOT NULL AND ds_email_destino_p::text <> '') then
	begin

	Insert into autorizacao_cirurgia_envio(
			nr_sequencia,
			nr_seq_autorizacao,
			dt_atualizacao,
			nm_usuario,
			dt_envio,
			ds_destino)
		values (	nextval('autorizacao_cirurgia_envio_seq'),
			nr_seq_autorizacao_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			SUBSTR(ds_email_destino_p,1,255));

	update  autorizacao_cirurgia_arq
    set     ie_anexar_email = 'N',
            nm_usuario      = nm_usuario_p,
            dt_atualizacao  = clock_timestamp()
    where   nr_seq_autorizacao = nr_seq_autorizacao_p;

	commit;
	ds_retorno_w := substr(obter_texto_tasy(129171, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end;
end if;

ds_mensagem_p	:= ds_retorno_w;

end;
$body$
