SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_email_avaliacao ( nr_seq_avaliacao_p bigint, ds_texto_email_p text, nm_usuario_p text ) AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(10);
cd_medico_w		varchar(10);
ds_remetente_w		varchar(255);
ds_destinatario_w	varchar(255);

nr_atendimento_w	bigint;
ds_texto_w		varchar(255);
			
			 
			 

BEGIN 
 
 
select	max(cd_medico), 
	max(cd_pessoa_fisica), 
	max(nr_atendimento) 
into STRICT	cd_medico_w, 
	cd_pessoa_fisica_w, 
	nr_atendimento_w 
from	med_avaliacao_paciente 
where	nr_sequencia = nr_seq_avaliacao_p;
 
 
if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
	 
	 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from 	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_w;
 
end if;
 
if (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') and (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
	 
 
	ds_remetente_w 		:= obter_compl_pf(cd_medico_w, 1 , 'M');
	ds_destinatario_w 	:= obter_compl_pf(cd_pessoa_fisica_w, 1 , 'M');
 
	if (coalesce(ds_remetente_w::text, '') = '') then 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(262019); -- O e-mail do remetente não foi informado! 
	end if;
 
	if (coalesce(ds_destinatario_w::text, '') = '') then 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(262021); -- O e-mail do destinatário não foi informado! 
	end if;
	 
	ds_texto_w	:= WHEB_MENSAGEM_PCK.get_texto(298974,null);
	 
 
	CALL enviar_email(	ds_texto_w,--'Avaliação', 
			ds_texto_email_p, 
			ds_remetente_w, 
			ds_destinatario_w, 
			nm_usuario_p, 
			'M');
	 
end if;	
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
