SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_integracao_opme (nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_opme_integracao_w		agenda_paciente.ie_opme_integracao%type;
cd_evento_log_w 			varchar(15);
ie_alteracao_w  			agenda_pac_consignado_hist.ie_alteracao%type;
ds_historico_w  			agenda_pac_consignado_hist.ds_historico%type;


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then
	select	coalesce(max(ie_opme_integracao),'N')
	into STRICT 		ie_opme_integracao_w
	from 		agenda_paciente
	where 	nr_sequencia = nr_seq_agenda_p;

	if (ie_opme_integracao_w = 'N') then
      ie_alteracao_w  := 'ENV';
		ds_historico_w  := substr(obter_desc_expressao_idioma(493102,'Identificação da necessidade de OPME',wheb_usuario_pck.get_nr_seq_idioma),1,2000);
      cd_evento_log_w := '0';
	else
      ie_alteracao_w  := 'RENV';
      ds_historico_w  := substr(obter_desc_expressao_idioma(493113,'Pedido de reenvio do agendamento',wheb_usuario_pck.get_nr_seq_idioma),1,2000);
		cd_evento_log_w := '9';
	end if;

	CALL ajusta_status_agenda_int_opme(nr_seq_agenda_p, cd_evento_log_w, '', nm_usuario_p);
	CALL gravar_hist_planeja_consig(nr_seq_agenda_p, ie_alteracao_w, ds_historico_w, nm_usuario_p);
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
