CREATE OR REPLACE PROCEDURE enviar_lote_audit_rec (nr_sequencia_p bigint, cd_convenio_p bigint, dt_referencia_p timestamp, nm_usuario_p text, ie_calcular_data_lote_p text, nr_seq_estagio_envio_p bigint) AS $body$
DECLARE


qt_dias_reap_w	bigint;


BEGIN


update 	lote_audit_recurso
set 	dt_envio 	= dt_referencia_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where 	nr_sequencia 	= nr_sequencia_p;

if (coalesce(ie_calcular_data_lote_p,'N') = 'S') then

	select 	coalesce(qt_dias_reapresentacao,0)
	into STRICT	qt_dias_reap_w
	from 	convenio
	where 	cd_convenio = cd_convenio_p;

	if (qt_dias_reap_w > 0) then
		update 	lote_audit_recurso
		set 	dt_prev_retorno = dt_referencia_p + qt_dias_reap_w
		where 	nr_sequencia 	= nr_sequencia_p;
	end if;


end if;

if (nr_seq_estagio_envio_p > 0) then
	CALL INSERIR_LOTE_AUDIT_REC_ESTAGIO(nm_usuario_p,nr_sequencia_p,nr_seq_estagio_envio_p);
end if;

commit;

end;
$body$
