SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_os_pos_vendas ( nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_cliente_w	com_cliente.nr_sequencia%type;
nm_usuario_gestor_w	varchar(50);
qt_existe_w		bigint;


BEGIN
	
	select	obter_usuario_gestor_conta(nr_seq_ordem_serv_p, 'S')
	into STRICT	nm_usuario_gestor_w
	;
	
	select	count(*)
	into STRICT	qt_existe_w
	from	man_ordem_servico_exec
	where	nr_seq_ordem 	= nr_seq_ordem_serv_p
	and	nm_usuario_exec = nm_usuario_gestor_w;
		
	if (qt_existe_w = 0) then
		insert into man_ordem_servico_exec(
				nr_sequencia,
				nr_seq_ordem,
				dt_atualizacao,
				nm_usuario,
				nm_usuario_exec,
				qt_min_prev,
				nr_seq_tipo_exec,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (	nextval('man_ordem_servico_exec_seq'),
				nr_seq_ordem_serv_p,
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_gestor_w,
				45,
				'3',
				clock_timestamp(),
				nm_usuario_p);
	end if;
	
	update	man_ordem_servico
	set	nr_seq_estagio = 371,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_ordem_serv_p;
	
	update	man_ordem_servico_exec
	set	dt_fim_execucao 	= clock_timestamp()
	where	nr_seq_ordem 		= nr_seq_ordem_serv_p
	and	nm_usuario_exec 	= nm_usuario_p
	and	coalesce(dt_fim_execucao::text, '') = '';
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
