CREATE OR REPLACE PROCEDURE enviar_pf_medico_hl7_atx ( cd_pessoa_fisica_p text, ie_acao_p text) AS $body$
DECLARE
ie_medico_w	bigint;
ds_sep_bv_w		varchar(100);
ds_param_integ_hl7_w	varchar(4000);

BEGIN
ds_sep_bv_w := obter_separador_bv;
select	count(*)
into STRICT	ie_medico_w
from	medico
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (ie_medico_w > 0) then
	ds_param_integ_hl7_w := 'cd_pessoa_fisica=' || cd_pessoa_fisica_p|| ds_sep_bv_w||
								'ie_acao='||ie_acao_p||ds_sep_bv_w;
	CALL gravar_agend_integracao(853, ds_param_integ_hl7_w); /* Tasy -> SCC (MFN_M02) - Doctor registration */
end if;
commit;

end;
