CREATE OR REPLACE PROCEDURE enviar_pix_copia_cola_sms (ds_mensagem_p text, ds_destinatario_p text, nm_usuario_p text) AS $body$
DECLARE


	nr_telefone_origem_w pessoa_fisica.nr_telefone_celular%type;
    id_sms_w bigint;
	

BEGIN
	
	select max(nr_telefone_celular)
    into STRICT   nr_telefone_origem_w
    from   pessoa_fisica
    where  nm_usuario = nm_usuario_p;

	id_sms_w := wheb_sms.enviar_sms(nr_telefone_origem_w, elimina_caractere_especial(ds_destinatario_p), ds_mensagem_p, nm_usuario_p, id_sms_w);		

	insert into log_envio_sms(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_envio, 
		nr_telefone, 
		ds_mensagem,
		id_sms)
	values (nextval('log_envio_sms_seq'), 
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		elimina_caractere_especial(ds_destinatario_p),
		ds_mensagem_p,
		id_sms_w);						
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
