SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_protocolo_fornec ( nr_seq_protocolo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_status_w		varchar(10);
nr_prescricao_w		bigint;


BEGIN

nr_seq_status_w := obter_param_usuario(10025, 4, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, nr_seq_status_w);

update  protocolo_fornec_opme
set     dt_retirada		=   clock_timestamp()
where   nr_sequencia		=   nr_seq_protocolo_p;


update  lote_producao b
set     b.nr_seq_status		=   nr_seq_status_w
where   b.nr_lote_producao in (SELECT distinct a.nr_lote_producao
				from     lote_producao_comp a
				where    a.nr_seq_protocolo = nr_seq_protocolo_p);

commit;

begin
select  max(b.nr_prescricao)
into STRICT	nr_prescricao_w
from    lote_producao_comp a,
	lote_producao b
where 	a.nr_lote_producao = b.nr_lote_producao
and	a.nr_seq_protocolo = nr_seq_protocolo_p;

CALL gerar_status_prescr_opm('F',nr_prescricao_w,'',cd_estabelecimento_p,nm_usuario_p);
exception
when others then
	null;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
