CREATE OR REPLACE PROCEDURE enviar_prot_conv_plamta ( nr_seq_protocolo_p bigint, ie_cancelar_envio_p text, nm_usuario_p text) AS $body$
BEGIN
 
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') and (ie_cancelar_envio_p IS NOT NULL AND ie_cancelar_envio_p::text <> '') then 
	begin 
		update 	protocolo_convenio 
		set	dt_envio = CASE WHEN ie_cancelar_envio_p='N' THEN  clock_timestamp() WHEN ie_cancelar_envio_p='S' THEN  null END , 
			nm_usuario_envio = CASE WHEN ie_cancelar_envio_p='N' THEN  nm_usuario_p WHEN ie_cancelar_envio_p='S' THEN  null END  
		where 	nr_seq_protocolo = nr_seq_protocolo_p;
		commit;
	end;
end if;	
 
end;
