CREATE OR REPLACE PROCEDURE enviar_sms_agenda_confirm ( ds_remetente_p text, ds_destinatario_p text, ds_mensagem_p text, cd_agenda_p bigint, nr_seq_agenda_p bigint, ie_confirmar_p text, nm_usuario_p text, ds_msg_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w	varchar(255) := '';


BEGIN
if (ds_destinatario_p IS NOT NULL AND ds_destinatario_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	ds_erro_w := Enviar_SMS_Agenda_Cons(
		ds_remetente_p, ds_destinatario_p, ds_mensagem_p, cd_agenda_p, nr_seq_agenda_p, nm_usuario_p, ds_erro_w);

	if (ie_confirmar_p = 'S') and (coalesce(ds_erro_w::text, '') = '') then
		begin

		update	agenda_consulta
		set	dt_confirmacao 		= clock_timestamp(),
			nm_usuario_confirm 	= nm_usuario_p
		where	nr_sequencia		= nr_seq_agenda_p;
		end;
	end if;

	ds_msg_erro_p := ds_erro_w;

	end;
end if;
commit;
end;
