SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_sms_atend ( ds_remetente_p text, ds_destinatario_p text, ds_mensagem_p text, nr_seq_regra_envio_p bigint, nr_atendimento_p bigint, ie_envio_retorno_p text, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
id_sms_w		bigint;
nr_celular_w	varchar(50);
nr_pos_ddd0_w			bigint := 0;
ie_utilizar_ddi_w varchar(1);


BEGIN

ie_utilizar_ddi_w := OBTER_VALOR_PARAM_USUARIO(0,214,0,obter_usuario_ativo,obter_estabelecimento_ativo);

nr_celular_w := trim(both ds_destinatario_p);

nr_celular_w := replace(nr_celular_w,'(','');
nr_celular_w := replace(nr_celular_w,')','');
nr_celular_w := replace(nr_celular_w,' ','');
nr_celular_w := replace(nr_celular_w,'-','');

if coalesce(ie_utilizar_ddi_w,'S') = 'S' Then
begin
	if (substr(nr_celular_w,1,2) <> '55') then
		nr_celular_w :=  '55'||nr_celular_w;
	end if;

	nr_pos_ddd0_w	:= position('550' in nr_celular_w);

	if (nr_pos_ddd0_w > 0)then
		nr_celular_w	:= replace(nr_celular_w,'550','55');
	end if;

	if (substr(nr_celular_w,1,3) = '550')then
		nr_celular_w := '55'||substr(nr_celular_w,4,50);
end if;
end;
end if;

if (ds_remetente_p IS NOT NULL AND ds_remetente_p::text <> '') and (nr_celular_w IS NOT NULL AND nr_celular_w::text <> '') and (ds_mensagem_p IS NOT NULL AND ds_mensagem_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	/* enviar sms */

	id_sms_w := wheb_sms.enviar_sms(ds_remetente_p, nr_celular_w, ds_mensagem_p, nm_usuario_p, id_sms_w);

	/* gravar log */

	select	nextval('atend_regra_sms_seq')
	into STRICT	nr_sequencia_w
	;

	insert into atend_regra_sms(
		nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario,
		ds_mensagem,
		nr_seq_id_sms,
		nr_seq_regra_envio,
		nr_atendimento,
		ie_envio_retorno)
	values (
		nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_mensagem_p,
		id_sms_w,
		nr_seq_regra_envio_p,
		nr_atendimento_p,
		ie_envio_retorno_p);

end if;
if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
