SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE envia_ci_final_cot_comp ( nr_cot_compra_p bigint, cd_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_regra_w			bigint;
nm_usuarios_adic_w		varchar(255);

cd_setor_regra_usuario_w		integer;
ds_perfil_adicional_w		varchar(4000) := '';
nm_usuario_w			varchar(15) := '';
ds_setor_adicional_w		varchar(2000) := '';
cd_perfil_usuario_w			integer;
ds_material_w			varchar(255);

ds_titulo_w			varchar(80);
ds_comunicado_w			varchar(4000);
cd_perfil_w			varchar(10);
ie_ci_lida_w			varchar(1);
nm_usuario_destino_w		varchar(255);
nr_seq_classif_w			bigint;
nr_seq_comunic_w			bigint;

ds_perfil_enviar_w			varchar(4000);
nm_usuarios_evento_w		varchar(255);
nm_usuario_adicional_w		varchar(4000);
cd_setor_evento_w			integer;
nr_atendimento_w			material_atend_paciente.nr_atendimento%type;
nr_seq_autorizacao_w		autorizacao_cirurgia.nr_sequencia%type;
nm_paciente_w			pessoa_fisica.nm_pessoa_fisica%type;
ds_razao_social_w		pessoa_juridica.ds_razao_social%type;

C01 CURSOR FOR 
	SELECT	b.nr_sequencia, 
		b.nm_usuarios_adic 
	from	regra_envio_comunic_compra a, 
		regra_envio_comunic_evento b 
	where	a.nr_sequencia = b.nr_seq_regra 
	and	a.cd_funcao = 915 
	and	b.cd_evento = 78 
	and	b.ie_situacao = 'A' 
	and	cd_estabelecimento = cd_estabelecimento_p 
	and	substr(obter_se_envia_ci_regra_compra(b.nr_sequencia,null,'CC',obter_perfil_ativo,nm_usuario_p,null),1,1) = 'S';

C02 CURSOR FOR 
	SELECT	coalesce(a.cd_setor_atendimento,0), 
		coalesce(a.cd_perfil,0), 
		coalesce(a.nm_usuario_envio,'0') 
	from	regra_envio_comunic_usu a 
	where	a.nr_seq_evento = nr_seq_regra_w;


BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_seq_regra_w, 
	nm_usuarios_adic_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	open C02;
	loop 
	fetch C02 into 
		cd_setor_regra_usuario_w, 
		cd_perfil_usuario_w, 
		nm_usuario_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
 
		if (cd_setor_regra_usuario_w <> 0) and (obter_se_contido_char(cd_setor_regra_usuario_w, ds_setor_adicional_w) = 'N') then 
			ds_setor_adicional_w := substr(ds_setor_adicional_w || cd_setor_regra_usuario_w || ',',1,2000);
		end if;
		if (cd_perfil_usuario_w <> 0) and (obter_se_contido_char(cd_perfil_usuario_w, ds_perfil_adicional_w) = 'N') then 
			ds_perfil_adicional_w := substr(ds_perfil_adicional_w || cd_perfil_usuario_w || ',',1,4000);
		end if;
		if (nm_usuario_w <> '0') and (obter_se_contido_char(nm_usuario_w, nm_usuario_adicional_w) = 'N') then 
			begin 
			nm_usuario_adicional_w := substr(nm_usuario_adicional_w || nm_usuario_w || ',',1,4000);
			end;
		end if;
		end;
	end loop;
	close C02;
	 
	begin 
	select	max(a.nr_atendimento), 
		max(a.nr_sequencia), 
		substr(max(coalesce(obter_nome_pf(a.cd_pessoa_fisica),coalesce(obter_nome_paciente_agenda(a.nr_seq_agenda),obter_dados_atendimento(a.nr_atendimento,'NP')))),1,255) nm_paciente 
	into STRICT	nr_atendimento_w, 
		nr_seq_autorizacao_w, 
		nm_paciente_w 
	from	autorizacao_cirurgia a, 
		material_autor_cirurgia b 
	where	a.nr_sequencia = b.nr_seq_autorizacao 
	and	b.nr_cot_compra = nr_cot_compra_p 
	and	b.cd_material = cd_material_p;
	exception 
	when others then 
		nm_paciente_w := '';
	end;
	 
	select	ds_material 
	into STRICT	ds_material_w 
	from	material 
	where	cd_material = cd_material_p;
	 
	select	coalesce(max(ds_razao_social),'') 
	into STRICT	ds_razao_social_w 
	from	pessoa_juridica 
	where	cd_cgc = nm_usuario_p; /*O usuario nesse caso sempre é o CNPJ do fornecedor que acessa pelo portal.*/
	 
	select	max(ds_titulo), 
		max(ds_comunicacao), 
		max(cd_perfil), 
		max(substr(nm_usuarios_adic,1,4000)), 
		max(cd_setor_destino) 
	into STRICT	ds_titulo_w, 
		ds_comunicado_w, 
		cd_perfil_w, 
		nm_usuarios_evento_w, 
		cd_setor_evento_w 
	from	regra_envio_comunic_evento 
	where	nr_sequencia = nr_seq_regra_w;
	 
	ds_titulo_w := substr(ds_titulo_w, 1, 80);
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@cd_material',cd_material_p), 1, 4000);
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@nr_atendimento',nr_atendimento_w), 1, 4000);	
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@nr_seq_autorizacao',nr_seq_autorizacao_w), 1, 4000);	
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@nm_paciente',nm_paciente_w), 1, 4000);	
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@cotacao',nr_cot_compra_p), 1, 4000);	
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@cnpj',nm_usuario_p), 1, 4000); /*O usuario nesse caso sempre é o CNPJ do fornecedor que acessa pelo portal.*/
	
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@razao_pj',ds_razao_social_w), 1, 4000);		
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@ds_material',ds_material_w), 1, 4000);		
	 
	select	coalesce(ie_ci_lida,'N') 
	into STRICT	ie_ci_lida_w 
	from 	regra_envio_comunic_evento 
	where 	nr_sequencia = nr_seq_regra_w;
 
	if (cd_setor_evento_w <> 0) then 
		begin 
		ds_setor_adicional_w := substr(ds_setor_adicional_w || cd_setor_evento_w || ',',1,4000);
		end;
	end if;
	 
	if (nm_usuarios_evento_w IS NOT NULL AND nm_usuarios_evento_w::text <> '') then 
		begin 
		nm_usuario_adicional_w := substr(nm_usuario_adicional_w || nm_usuarios_evento_w || ',',1,4000);
		end;
	end if;
	 
	nm_usuario_destino_w := nm_usuario_adicional_w;
 
	if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') then 
 
		select	obter_classif_comunic('F') 
		into STRICT	nr_seq_classif_w 
		;
 
		select	nextval('comunic_interna_seq') 
		into STRICT	nr_seq_comunic_w 
		;
 
		if (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then 
			ds_perfil_adicional_w := substr(ds_perfil_adicional_w || cd_perfil_w ||',',1,4000);
		end if;
 
		insert into comunic_interna( 
			dt_comunicado, 
			ds_titulo, 
			ds_comunicado, 
			nm_usuario, 
			dt_atualizacao, 
			ie_geral, 
			nm_usuario_destino, 
			nr_sequencia, 
			ie_gerencial, 
			nr_seq_classif, 
			dt_liberacao, 
			nr_seq_resultado, 
			ds_perfil_adicional, 
			ds_setor_adicional) 
		values (	clock_timestamp(), 
			ds_titulo_w, 
			ds_comunicado_w, 
			nm_usuario_p, 
			clock_timestamp(), 
			'N', 
			nm_usuario_destino_w, 
			nr_seq_comunic_w, 
			'N', 
			nr_seq_classif_w, 
			clock_timestamp(), 
			null, 
			ds_perfil_adicional_w, 
			ds_setor_adicional_w);
 
		if (ie_ci_lida_w = 'S') then 
			insert into comunic_interna_lida(nr_sequencia,nm_usuario,dt_atualizacao)values (nr_seq_comunic_w,nm_usuario_p,clock_timestamp());
		end if;
 
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE envia_ci_final_cot_comp ( nr_cot_compra_p bigint, cd_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

