CREATE OR REPLACE PROCEDURE envia_ci_rev_cont_todos_estab (cd_empresa_p bigint) AS $body$
DECLARE

 
cd_estabelecimento_w		integer;

c01 CURSOR FOR 
SELECT	cd_estabelecimento 
from	estabelecimento_v 
where	cd_empresa = cd_empresa_p 
and	ie_situacao = 'A';


BEGIN 
 
open c01;
loop 
fetch c01 into 
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	CALL envia_ci_revisao_contrato(cd_estabelecimento_w);
 
	end;
end loop;
close c01;
 
end;
$body$
