CREATE OR REPLACE PROCEDURE envia_ci_tipo_baixa_req (nr_requisicao_p bigint, nr_sequencia_p bigint, cd_motivo_baixa_p bigint, nm_usuario_p text, ds_observacao_p text) AS $body$
DECLARE


ds_motivo_baixa_w				varchar(255);
nm_usuario_destino_w			varchar(15);
ds_titulo_w				varchar(80);
ds_comunicacao_w				varchar(2000);
ds_lista_material_w				varchar(2000);
nr_seq_classif_w				bigint;
cd_material_w				integer;
ds_material_w				varchar(255);



BEGIN
select	substr(obter_desc_motivo_baixa_req(cd_motivo_baixa_p),1,80)
into STRICT	ds_motivo_baixa_w
;

select	obter_usuario_pessoa(cd_pessoa_requisitante)
into STRICT	nm_usuario_destino_w
from	requisicao_material
where	nr_requisicao = nr_requisicao_p;

ds_titulo_w		:= wheb_mensagem_pck.get_texto(299982,'DS_RETORNO='||nr_requisicao_p);

select	cd_material,
	substr(obter_desc_material(cd_material),1,255)
into STRICT	cd_material_w,
	ds_material_w
from	item_requisicao_material
where	nr_requisicao   = nr_requisicao_p
and	nr_sequencia	= nr_sequencia_p;

ds_comunicacao_w := substr(wheb_mensagem_pck.get_texto(299985,'DS_RETORNO1='||nr_requisicao_p||';DS_RETORNO2='||ds_motivo_baixa_w||';DS_RETORNO3='||cd_material_w||';DS_RETORNO4='||ds_material_w||';DS_RETORNO5='||ds_observacao_p),1,2000);

if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') then

	select	max(obter_classif_comunic('F'))
	into STRICT	nr_seq_classif_w
	;

	CALL Gerar_Comunic_Padrao(
		clock_timestamp(),
		ds_titulo_w,
		ds_comunicacao_w,
		nm_usuario_p,
		'N',
		nm_usuario_destino_w,
		'N',
		nr_seq_classif_w,
		'','','',clock_timestamp(),'','');
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
