SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE envia_comunicado_hist_atend ( nm_usuario_p text, nm_usuario_destino_p text, nr_sequencia_p bigint) AS $body$
DECLARE

 
nr_atendimento_w	bigint;
nr_seq_tipo_historico_w bigint;
ds_observacao_w     varchar(1900);
nm_pessoa_fisica_w	varchar(60);
ds_tipo_historico_w	varchar(90);
ds_titulo_w		varchar(80);
ds_comunicado_w		varchar(2000);
nr_seq_classif_w	bigint;
				

BEGIN 
 
if (coalesce(nr_sequencia_p,0) > 0) and ((coalesce(nm_usuario_destino_p,null) IS NOT NULL AND (coalesce(nm_usuario_destino_p,null))::text <> '')) then 
 
 
	select	nr_atendimento, 
		nr_seq_tipo_historico, 
		substr(ds_observacao,1,1900) 
	into STRICT	nr_atendimento_w, 
		nr_seq_tipo_historico_w, 
		ds_observacao_w 
	from	atendimento_hist_paciente 
	where	nr_sequencia = nr_sequencia_p;
 
	select	substr(obter_nome_pf(cd_pessoa_fisica),1,60) 
	into STRICT	nm_pessoa_fisica_w 
	from	atendimento_paciente 
	where 	nr_atendimento = nr_atendimento_w;
	 
	select obter_desc_tipo_hist_atend(nr_seq_tipo_historico_w) 
	into STRICT	ds_tipo_historico_w 
	;
	 
	ds_titulo_w := wheb_mensagem_pck.get_texto(307419) || ' ' || lower(wheb_mensagem_pck.get_texto(307420)) || ' '|| nr_atendimento_w ||' - ' ||ds_tipo_historico_w; -- Histórico atendimento 
	
	ds_comunicado_w := substr(wheb_mensagem_pck.get_texto(307421,	'NM_PESSOA_FISICA_W=' || nm_pessoa_fisica_w || ';' || 
																	'DS_TIPO_HISTORICO_W=' || ds_tipo_historico_w || ';' || 
																	'DT_ATUAL=' || to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss') || ';' || 
																	'DS_OBSERVACAO_W=' || ds_observacao_w), 
								1,2000);
														/* 
															Paciente: #@NM_PESSOA_FISICA_W#@ 
															Tipo de histórico: #@DS_TIPO_HISTORICO_W#@ 
															Data/hora: #@DT_ATUAL#@ 
															Observação: #@DS_OBSERVACAO_W#@ 
														*/
 
	select	max(obter_classif_comunic('F')) 
	into STRICT	nr_seq_classif_w 
	;
 
	CALL Gerar_Comunic_Padrao( 
		clock_timestamp(), 
		ds_titulo_w, 
		ds_comunicado_w, 
		nm_usuario_p, 
		'N', 
		nm_usuario_destino_p, 
		'N', 
		nr_seq_classif_w, 
		'','','',clock_timestamp(),'','');
	 
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
