SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE envia_comunic_altera_entr_oc ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ds_entrega_original_p text, ds_entrega_atual_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_pessoa_solicitante_w			varchar(100);
ds_comprador_w				comprador.nm_guerra%type;
cd_estabelecimento_w			smallint;
nr_seq_regra_w				bigint;
cd_material_w				integer;
ds_material_w				varchar(255);
nm_usuario_destino_w			varchar(255);
ds_titulo_w				varchar(80);
ds_comunicado_w				varchar(4000);
nr_seq_classif_w				bigint;
qt_regra_usuario_w				bigint;
nr_seq_comunic_w				bigint;
cd_perfil_w				varchar(10);
ie_ci_lida_w				varchar(1);
/* Se tiver setor na regra, envia CI para os setores */
ds_setor_adicional_w          	 	varchar(2000) := '';
/* Campos da regra Usuário da Regra */
cd_setor_regra_usuario_w			integer;

c05 CURSOR FOR 
SELECT	coalesce(a.cd_setor_atendimento,0) cd_setor_atendimento 
from	regra_envio_comunic_usu a 
where	a.nr_seq_evento = nr_seq_regra_w;


BEGIN 
 
select	substr(obter_nome_pf_pj(a.cd_pessoa_solicitante,null),1,100), 
	substr(sup_obter_nome_comprador(a.cd_estabelecimento, a.cd_comprador),1,80), 
	a.cd_estabelecimento, 
	b.cd_material, 
	substr(obter_desc_material(b.cd_material),1,255) ds_material 
into STRICT	ds_pessoa_solicitante_w, 
	ds_comprador_w, 
	cd_estabelecimento_w, 
	cd_material_w, 
	ds_material_w 
from	ordem_Compra a, 
	ordem_compra_item b 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	b.nr_item_oci = nr_item_oci_p 
and	a.nr_ordem_compra = nr_ordem_compra_p;
 
select	coalesce(max(b.nr_sequencia), 0), 
	max(cd_perfil) 
into STRICT	nr_seq_regra_w, 
	cd_perfil_w 
from	regra_envio_comunic_compra a, 
	regra_envio_comunic_evento b 
where	a.nr_sequencia = b.nr_seq_regra 
and	a.cd_funcao = 917 
and	b.cd_evento = 21 
and	b.ie_situacao = 'A' 
and	cd_estabelecimento = cd_estabelecimento_w 
and	substr(obter_se_envia_ci_regra_compra(b.nr_sequencia,nr_ordem_compra_p,'OC',obter_perfil_ativo,nm_usuario_p,null),1,1) = 'S';
 
if (nr_seq_regra_w > 0) then 
 
	open C05;
	loop 
	fetch C05 into	 
		cd_setor_regra_usuario_w;
	EXIT WHEN NOT FOUND; /* apply on C05 */
		begin 
		if (cd_setor_regra_usuario_w <> 0) and (obter_se_contido_char(cd_setor_regra_usuario_w, ds_setor_adicional_w) = 'N') then 
			ds_setor_adicional_w := substr(ds_setor_adicional_w || cd_setor_regra_usuario_w || ',',1,2000);
		end if;
		end;
	end loop;
	close C05;
 
	select	max(ds_titulo), 	 
		max(ds_comunicacao) 
	into STRICT	ds_titulo_w, 
		ds_comunicado_w 
	from	regra_envio_comunic_evento 
	where	nr_sequencia = nr_seq_regra_w;
	 
	ds_titulo_w := substr(ds_titulo_w, 1, 80);
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@nr_ordem', nr_ordem_compra_p), 1, 4000);
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@item', nr_item_oci_p), 1, 4000); 	
	 
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@cd_material', cd_material_w), 1, 4000); 		
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@ds_material', ds_material_w), 1, 4000); 		
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@solicitante', ds_pessoa_solicitante_w), 1, 4000); 		
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@comprador', ds_comprador_w), 1, 4000); 		
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@entregas_originais', ds_entrega_original_p), 1, 4000); 		
	ds_comunicado_w := substr(replace_macro(ds_comunicado_w, '@entregas_atuais', ds_entrega_atual_p), 1, 4000); 				
 
	select	coalesce(ie_ci_lida,'N') 
	into STRICT	ie_ci_lida_w 
	from 	regra_envio_comunic_evento 
	where 	nr_sequencia = nr_seq_regra_w;
 
	select	count(*) 
	into STRICT	qt_regra_usuario_w 
	from	regra_envio_comunic_compra a, 
		regra_envio_comunic_evento b, 
		regra_envio_comunic_usu c 
	where	a.nr_sequencia = b.nr_seq_regra 
	and	b.nr_sequencia = c.nr_seq_evento 
	and	b.nr_sequencia = nr_seq_regra_w;
 
	if (qt_regra_usuario_w > 0) then 
		nm_usuario_destino_w := obter_usuarios_comunic_compras(nr_ordem_compra_p, null, 21, nr_seq_regra_w, nm_usuario_p);
	end if;
	 
	if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') then 
 
		select	obter_classif_comunic('F') 
		into STRICT	nr_seq_classif_w 
		;	
			 
		select	nextval('comunic_interna_seq') 
		into STRICT	nr_seq_comunic_w 
		;
		 
		if (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then 
			cd_perfil_w := cd_perfil_w ||',';
		end if;
 
		insert into comunic_interna( 
			dt_comunicado, 
			ds_titulo, 
			ds_comunicado, 
			nm_usuario, 
			dt_atualizacao, 
			ie_geral, 
			nm_usuario_destino, 
			nr_sequencia, 
			ie_gerencial, 
			nr_seq_classif, 
			dt_liberacao, 
			ds_perfil_adicional, 
			ds_setor_adicional) 
		values (	clock_timestamp(), 
			ds_titulo_w, 
			ds_comunicado_w, 
			nm_usuario_p, 
			clock_timestamp(), 
			'N', 
			nm_usuario_destino_w, 
			nr_seq_comunic_w, 
			'N', 
			nr_seq_classif_w, 
			clock_timestamp(), 
			cd_perfil_w, 
			ds_setor_adicional_w);
 
		/*Para que a comunicação seja gerada como lida ao próprio usuário */
 
		if (ie_ci_lida_w = 'S') then 
			insert into comunic_interna_lida(nr_sequencia,nm_usuario,dt_atualizacao)values (nr_seq_comunic_w,nm_usuario_p,clock_timestamp());
		end if;
 
	end if;	
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
