CREATE OR REPLACE PROCEDURE envia_confimacao_fila_transm ( nr_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*	Procedure usada para enviar a confirmação da geração da ordem de compra para a Fila de Transmissão que existe no Gerenciador de Suprimentos.*/

BEGIN

update	cot_compra
set	ie_status_envio	= 'F'
where	nr_cot_compra	= nr_cot_compra_p;

insert into cot_compra_fila_transm(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_cot_compra,
	ie_evento)
values (	nextval('cot_compra_fila_transm_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_cot_compra_p,
	'EC');

insert into cot_compra_log_integracao(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_cot_compra,
	ie_status_envio,
	ie_tipo_integracao,
	ds_log)
values (	nextval('cot_compra_log_integracao_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_cot_compra_p,
	'FC',
	'C',
	wheb_mensagem_pck.get_texto(299987));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
