CREATE OR REPLACE PROCEDURE envia_email_agrup_compra ( ie_tipo_mensagem_p bigint) AS $body$
DECLARE


ds_destino_w		varchar(255);

nr_seq_envio_w		bigint;
ds_assunto_w		varchar(255);
ds_mensagem_w		varchar(4000);
ds_email_origem_w		varchar(255);
nm_usuario_origem_w	varchar(255);

c01 CURSOR FOR
SELECT	nr_sequencia,
	ds_assunto,
	ds_mensagem,
	ds_email_origem,
	nm_usuario_origem
from	envio_email_compra_agrup a
where	ie_tipo_mensagem = ie_tipo_mensagem_p
and	coalesce(ie_cancelado,'N') = 'N'
and	exists (select	1
		from	email_compra_agrup_dest x
		where	x.nr_seq_envio = a.nr_sequencia
		and	coalesce(x.ie_enviado,'N') = 'N');

c02 CURSOR FOR
SELECT	ds_destino
from	email_compra_agrup_dest
where	nr_seq_envio = nr_seq_envio_w
and	coalesce(ie_enviado,'N') = 'N'
group by	ds_destino;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_envio_w,
	ds_assunto_w,
	ds_mensagem_w,
	ds_email_origem_w,
	nm_usuario_origem_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	open c02;
	loop
	fetch c02 into
		ds_destino_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		begin
		CALL enviar_email(ds_assunto_w,ds_mensagem_w,ds_email_origem_w,ds_destino_w,null,'M');

		update	email_compra_agrup_dest
		set	dt_envio = clock_timestamp(),
			ie_enviado = 'S'
		where	nr_seq_envio = nr_seq_envio_w
		and	ds_destino = ds_destino_w;

		exception
		when others then
			/*gravar__log__tasy(17711,'Falha ao enviar da Gest√£o de Envio do Compras - Seq. Men.: ' || nr_seq_envio_w || ' Destino: ' || ds_destino_w,'TASY');*/

			nr_seq_envio_w := nr_seq_envio_w;
		end;

		end;
	end loop;
	close c02;

	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
