SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE envia_email_fornec ( nr_cot_compra_forn_p bigint, cd_cgc_fornec_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
 
nr_seq_regra_w		bigint;
ds_email_remetente_w	varchar(255);
ds_email_adicional_w	varchar(2000);
cd_perfil_dispara_w		integer;
ie_momento_envio_w	varchar(1);
ie_envia_pessoa_deleg_w	varchar(1);
ds_assunto_w		varchar(255);
ds_mensagem_w		varchar(4000);
nm_usuario_receb_w	varchar(15);
ds_email_w		varchar(255);
ie_existe_w		varchar(1);
ds_destinatarios_w		varchar(4000);
ds_email_origem_w		varchar(255);
nm_usuario_origem_w	varchar(255);
ie_usuario_w		regra_envio_email_compra.ie_usuario%type;
cd_comprador_w		cot_compra.cd_comprador%type;

C01 CURSOR FOR 
	SELECT	coalesce(nr_sequencia,0), 
		coalesce(ds_email_remetente,'X'), 
		replace(ds_email_adicional,',',';'), 
		cd_perfil_disparar, 
		coalesce(ie_momento_envio,'I'), 
		coalesce(ie_envia_pessoa_deleg,'N') 
	from	regra_envio_email_compra 
	where	ie_tipo_mensagem = 95 
	and	ie_situacao = 'A' 
	and	cd_estabelecimento = cd_estabelecimento_p;

C03 CURSOR FOR 
	SELECT	nm_usuario_receb 
	from	regra_envio_email_usu 
	where	nr_seq_regra = nr_seq_regra_w 
	and	(nm_usuario_receb IS NOT NULL AND nm_usuario_receb::text <> '');


BEGIN 
 
select	cd_comprador 
into STRICT	cd_comprador_w 
from	cot_compra 
where	nr_cot_compra = nr_cot_compra_forn_p;
 
open C01;
loop 
fetch C01 into 
	nr_seq_regra_w, 
	ds_email_remetente_w, 
	ds_email_adicional_w, 
	cd_perfil_dispara_w, 
	ie_momento_envio_w, 
	ie_envia_pessoa_deleg_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if	((coalesce(cd_perfil_dispara_w::text, '') = '') or 
		(cd_perfil_dispara_w IS NOT NULL AND cd_perfil_dispara_w::text <> '' AND cd_perfil_dispara_w = obter_perfil_ativo)) then 
		begin 
		 
		select	max(ds_assunto), 
			max(ds_mensagem_padrao), 
			coalesce(max(ie_usuario),'U') 
		into STRICT	ds_assunto_w, 
			ds_mensagem_w, 
			ie_usuario_w 
		from	regra_envio_email_compra 
		where	nr_sequencia = nr_seq_regra_w;
		 
		select	max(nm_usuario) 
		into STRICT	nm_usuario_origem_w 
		from	usuario 
		where	nm_usuario = nm_usuario_p;
		 
		if (ie_usuario_w = 'U') then 
			begin 
			select	max(ds_email) 
			into STRICT	ds_email_origem_w 
			from	usuario 
			where	nm_usuario = nm_usuario_p;
			end;
		elsif (ie_usuario_w = 'C') then --Setor compras 
			begin 
			select	max(ds_email) 
			into STRICT	ds_email_origem_w 
			from	parametro_compras 
			where	cd_estabelecimento = cd_estabelecimento_p;
			end;
		elsif (ie_usuario_w = 'O') then --Comprador 
			begin 
 
			select	max(ds_email) 
			into STRICT	ds_email_origem_w 
			from	comprador 
			where	cd_pessoa_fisica = cd_comprador_w 
			and	cd_estabelecimento = cd_estabelecimento_p;
			end;
		end if;	
 
		select	substr(coalesce(max(b.ds_email),max(a.ds_email)),1,2000) 
		into STRICT	ds_destinatarios_w 
		FROM pessoa_juridica a
LEFT OUTER JOIN pessoa_juridica_estab b ON (a.cd_cgc = b.cd_cgc)
WHERE coalesce(b.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p and a.cd_cgc = cd_cgc_fornec_p;
 
		open C03;
		loop 
		fetch C03 into 
			nm_usuario_receb_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin 
 
			select	max(ds_email) 
			into STRICT	ds_email_w 
			from	usuario 
			where	nm_usuario = nm_usuario_receb_w;
 
			if (ds_email_w IS NOT NULL AND ds_email_w::text <> '') then 
 
				select	coalesce(max('S'),'N') 
				into STRICT	ie_existe_w 
				 
				where	upper(ds_destinatarios_w) like upper('%' || ds_email_w || '%');
 
				if (ie_existe_w = 'N') then 
					if (coalesce(ds_destinatarios_w::text, '') = '') then 
						ds_destinatarios_w	:= ds_email_w || ';';
					else 
						ds_destinatarios_w	:= ds_destinatarios_w || ';' || ds_email_w || ';';
					end if;
				end if;
			end if;
			end;
		end loop;
		close C03;
 
		ds_assunto_w	:= replace_macro(ds_assunto_w, '@cotacao', nr_cot_compra_forn_p);
		ds_assunto_w	:= substr(ds_assunto_w, 1, 255);
 
		ds_mensagem_w	:= replace_macro(ds_mensagem_w, '@cotacao', nr_cot_compra_forn_p);
		ds_mensagem_w	:= substr(ds_mensagem_w, 1, 4000);
 
		if (ds_email_adicional_w IS NOT NULL AND ds_email_adicional_w::text <> '') then 
			if (coalesce(ds_destinatarios_w::text, '') = '') then 
				ds_destinatarios_w := ds_email_adicional_w || ';';
			else 
				ds_destinatarios_w := ds_email_adicional_w || ';' || ds_destinatarios_w;
			end if;
		end if;
 
			if (ie_momento_envio_w = 'A') then 
				begin 
 
				CALL sup_grava_envio_email( 
     --286320 = Cotação 
					obter_desc_expressao(286320), 
					'95', 
					nr_cot_compra_forn_p, 
					0, 
					0, 
					ds_destinatarios_w, 
					nm_usuario_origem_w, 
					ds_email_origem_w, 
					ds_assunto_w, 
					ds_mensagem_w, 
					cd_estabelecimento_p, 
					nm_usuario_p);
 
				end;
			else 
				begin 
				 
				CALL enviar_email(ds_assunto_w,ds_mensagem_w,ds_email_origem_w,ds_destinatarios_w,nm_usuario_origem_w,'M');
				 
				exception 
				when others then 
					nr_seq_regra_w := nr_seq_regra_w;
				end;
			end if;
 
		end;
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE envia_email_fornec ( nr_cot_compra_forn_p bigint, cd_cgc_fornec_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

