SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE envia_email_solic_reprov ( nr_seq_aprovacao_p bigint, nr_seq_proc_aprov_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* Dados da regra */
 
nr_seq_regra_w			bigint;
ds_email_adicional_w		varchar(2000);
cd_perfil_dispara_w		integer;
ie_momento_envio_w		varchar(1);
ie_usuario_w			varchar(1);
nr_documento_w			bigint;
ds_email_remetente_w		varchar(255);

/* Dados da macro */
 
nm_solicitante_w		varchar(255);
cd_centro_custo_w		integer;
ds_centro_custo_w		varchar(255);
cd_local_estoque_w		smallint;
ds_local_estoque_w		varchar(255);
nm_fornecedor_w			varchar(255);
ds_tipo_servico_w		varchar(255);
dt_solicitacao_compra_w		timestamp;
dt_liberacao_w			timestamp;
ds_observacao_w			varchar(4000);
ds_materiais_w			varchar(4000);
nm_usuario_reprov_w		varchar(15);
nm_pessoa_reprov_w		varchar(255);
dt_reprovacao_w			timestamp;
ds_motivo_reprov_w		varchar(255);
ds_justificativa_w		varchar(2000);
vl_total_aprov_w		double precision;
vl_total_aprov_masc_w		varchar(100);

/*Dados do e-mail*/
 
ds_assunto_w			varchar(255);
ds_mensagem_w			varchar(4000);
ds_destinatarios_w		varchar(4000);
nm_usuario_origem_w		varchar(255);
ds_email_origem_w		varchar(255);
cd_comprador_w			varchar(10);


BEGIN 
 
select	coalesce(max(nr_solic_compra),0) 
into STRICT	nr_documento_w 
from	solic_compra_item 
where	nr_seq_aprovacao = nr_seq_aprovacao_p;
 
select	coalesce(max(nr_sequencia),0), 
	coalesce(max(ds_email_remetente),'X'), 
	max(replace(ds_email_adicional,',',';')), 
	max(cd_perfil_disparar), 
	coalesce(max(ie_momento_envio),'I') 
into STRICT	nr_seq_regra_w, 
	ds_email_remetente_w, 
	ds_email_adicional_w, 
	cd_perfil_dispara_w, 
	ie_momento_envio_w 
from	regra_envio_email_compra 
where	ie_tipo_mensagem = 66 
and	ie_situacao = 'A' 
and	cd_estabelecimento = cd_estabelecimento_p 
and	obter_se_envia_email_regra(nr_documento_w, 'SC', ie_tipo_mensagem, cd_estabelecimento) = 'S';
 
if (nr_seq_regra_w > 0) and 
	((coalesce(cd_perfil_dispara_w::text, '') = '') or 
	(cd_perfil_dispara_w IS NOT NULL AND cd_perfil_dispara_w::text <> '' AND cd_perfil_dispara_w = obter_perfil_ativo)) then 
	begin 
 
	select	coalesce(cd_comprador_resp,0), 
		substr(obter_nome_pf(cd_pessoa_solicitante),1,255), 
		coalesce(cd_centro_custo,0), 
		substr(coalesce(obter_desc_centro_custo(cd_centro_custo),WHEB_MENSAGEM_PCK.get_texto(299740)),1,255), 
		coalesce(cd_local_estoque,0), 
		substr(coalesce(obter_desc_local_estoque(cd_local_estoque),WHEB_MENSAGEM_PCK.get_texto(299743)),1,255), 
		obter_nome_pf_pj(cd_pessoa_fisica,cd_fornec_sugerido), 
		substr(coalesce(obter_valor_dominio(4200,ie_tipo_servico),WHEB_MENSAGEM_PCK.get_texto(299744)),1,255), 
		dt_solicitacao_compra, 
		dt_liberacao, 
		ds_observacao, 
		SUBSTR(obter_select_concatenado_bv( 
			    'select cd_material||chr(45)||obter_desc_material(cd_material) from solic_compra_item where nr_solic_compra = :nr_solic_compra', 
			    'nr_solic_compra=' || nr_documento_w,','),1,255) 
	into STRICT	cd_comprador_w, 
		nm_solicitante_w, 
		cd_centro_custo_w, 
		ds_centro_custo_w, 
		cd_local_estoque_w, 
		ds_local_estoque_w, 
		nm_fornecedor_w, 
		ds_tipo_servico_w, 
		dt_solicitacao_compra_w, 
		dt_liberacao_w, 
		ds_observacao_w, 
		ds_materiais_w 
	from	solic_compra 
	where	nr_solic_compra = nr_documento_w;
		 
	select	nm_usuario_aprov, 
		substr(obter_nome_usuario(nm_usuario_aprov),1,255), 
		dt_definicao, 
		substr(obter_valor_dominio(1015,ie_motivo_reprovacao),1,255), 
		ds_observacao 
	into STRICT	nm_usuario_reprov_w, 
		nm_pessoa_reprov_w, 
		dt_reprovacao_w, 
		ds_motivo_reprov_w, 
		ds_justificativa_w 
	from	processo_aprov_compra 
	where	nr_sequencia = nr_seq_aprovacao_p 
	and	nr_seq_proc_aprov = nr_seq_proc_aprov_p;
 
	select	coalesce(sum(b.qt_material * coalesce(b.vl_unit_previsto,0)),0) 
	into STRICT	vl_total_aprov_w 
	from	solic_compra a, 
		solic_compra_item b 
	where	a.nr_solic_compra = b.nr_solic_compra 
	and	a.nr_solic_compra = nr_documento_w 
	and	b.nr_seq_aprovacao = nr_seq_aprovacao_p;
 
	vl_total_aprov_masc_w	:= substr(campo_mascara_virgula_casas(vl_total_aprov_w,2),1,100);
 
	select	ds_assunto, 
		ds_mensagem_padrao 
	into STRICT	ds_assunto_w, 
		ds_mensagem_w 
	from	regra_envio_email_compra 
	where	nr_sequencia = nr_seq_regra_w;
 
	ds_assunto_w	:=	substr( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro(ds_assunto_w, 
					'@nr_documento',nr_documento_w), 
					'@solicitante',nm_solicitante_w), 
					'@cd_local_estoque',cd_local_estoque_w), 
					'@ds_local_estoque',ds_local_estoque_w), 
					'@cd_centro_custo',cd_centro_custo_w), 
					'@ds_centro_custo',ds_centro_custo_w), 
					'@vl_total_aprovacao',vl_total_aprov_masc_w), 
					'@seq_aprovacao',nr_seq_aprovacao_p), 
					'@ds_forn_sugerido',nm_fornecedor_w), 
					'@ds_tipo_servico',ds_tipo_servico_w), 
					'@dt_solicitacao_compra',dt_solicitacao_compra_w), 
					'@dt_liberacao',dt_liberacao_w), 
					'@ds_observacao',ds_observacao_w), 
					'@ds_material',ds_materiais_w), 
					'@nm_usuario_reprov',nm_usuario_reprov_w), 
					'@nm_pessoa_reprov',nm_pessoa_reprov_w), 
					'@dt_reprovacao',dt_reprovacao_w), 
					'@ds_motivo_reprov',ds_motivo_reprov_w), 
					'@ds_justificativa',ds_justificativa_w),1,255);
 
	ds_mensagem_w	:=	substr( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro( 
				replace_macro(ds_mensagem_w, 
					'@nr_documento',nr_documento_w), 
					'@solicitante',nm_solicitante_w), 
					'@cd_local_estoque',cd_local_estoque_w), 
					'@ds_local_estoque',ds_local_estoque_w), 
					'@cd_centro_custo',cd_centro_custo_w), 
					'@ds_centro_custo',ds_centro_custo_w), 
					'@vl_total_aprovacao',vl_total_aprov_masc_w), 
					'@seq_aprovacao',nr_seq_aprovacao_p), 
					'@ds_forn_sugerido',nm_fornecedor_w), 
					'@ds_tipo_servico',ds_tipo_servico_w), 
					'@dt_solicitacao_compra',dt_solicitacao_compra_w), 
					'@dt_liberacao',dt_liberacao_w), 
					'@ds_observacao',ds_observacao_w), 
					'@ds_material',ds_materiais_w), 
					'@nm_usuario_reprov',nm_usuario_reprov_w), 
					'@nm_pessoa_reprov',nm_pessoa_reprov_w), 
					'@dt_reprovacao',dt_reprovacao_w), 
					'@ds_motivo_reprov',ds_motivo_reprov_w), 
					'@ds_justificativa',ds_justificativa_w),1,4000);
 
	select	coalesce(max(ie_usuario),'U') 
	into STRICT	ie_usuario_w 
	from	regra_envio_email_compra 
	where	nr_sequencia = nr_seq_regra_w;
 
	if (ie_usuario_w = 'U') or 
		((coalesce(cd_comprador_w,'0') <> 0) and (ie_usuario_w = 'O')) then --Usuario 
		begin 
 
		select	ds_email, 
			nm_usuario 
		into STRICT	ds_email_origem_w, 
			nm_usuario_origem_w 
		from	usuario 
		where	nm_usuario = nm_usuario_p;
 
		end;
	elsif (ie_usuario_w = 'C') then --Setor compras 
		begin 
 
		select	ds_email 
		into STRICT	ds_email_origem_w 
		from	parametro_compras 
		where	cd_estabelecimento = cd_estabelecimento_p;
 
		select	coalesce(ds_fantasia,ds_razao_social) 
		into STRICT	nm_usuario_origem_w 
		from	estabelecimento_v 
		where	cd_estabelecimento = cd_estabelecimento_p;
 
		end;
	elsif (ie_usuario_w = 'O') then --Comprador 
		begin 
 
		select	max(ds_email), 
			max(nm_guerra) 
		into STRICT	ds_email_origem_w, 
			nm_usuario_origem_w 
		from	comprador 
		where	cd_pessoa_fisica = cd_comprador_w 
		and	cd_estabelecimento = cd_estabelecimento_p;
 
		end;
	end if;
 
	if (ds_email_remetente_w <> 'X') then 
		ds_email_origem_w	:= ds_email_remetente_w;
	end if;
 
	ds_destinatarios_w		:= obter_emails_envio_compra(nr_documento_w, 
							'SC', 
							nr_seq_aprovacao_p, 
							nr_seq_proc_aprov_p, 
							nr_seq_regra_w, 
							cd_estabelecimento_p, 
							nm_usuario_p);
 
	if (ds_email_adicional_w IS NOT NULL AND ds_email_adicional_w::text <> '') then 
		ds_destinatarios_w	:= ds_destinatarios_w || ds_email_adicional_w;
	end if;
 
	if (ie_momento_envio_w = 'A') then 
		begin 
 
		CALL sup_grava_envio_email( 
			'SC', 
			'66', 
			nr_documento_w, 
			nr_seq_aprovacao_p, 
			nr_seq_proc_aprov_p, 
			ds_destinatarios_w, 
			nm_usuario_origem_w, 
			ds_email_origem_w, 
			ds_assunto_w, 
			ds_mensagem_w, 
			cd_estabelecimento_p, 
			nm_usuario_p);
 
		end;
	else 
		begin 
		CALL enviar_email(ds_assunto_w,ds_mensagem_w,ds_email_origem_w,ds_destinatarios_w,nm_usuario_origem_w,'M');
		exception 
		when others then 
			/*gravar__log__tasy(91301,'Falha ao enviar e-mail compras - Evento: 66 - Seq. Regra: ' || nr_seq_regra_w,nm_usuario_p);*/
 
			 
			 
			CALL gerar_hist_solic_sem_commit( 
					nr_documento_w, 
					WHEB_MENSAGEM_PCK.get_texto(299745), 
					WHEB_MENSAGEM_PCK.get_texto(299746,'NR_SEQ_REGRA_W=' || nr_seq_regra_w), 
					'FGE', 
					nm_usuario_p);
		end;
	end if;
 
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE envia_email_solic_reprov ( nr_seq_aprovacao_p bigint, nr_seq_proc_aprov_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

