CREATE OR REPLACE PROCEDURE envia_pesssoa_jur_fila_transm ( cd_cnpj_p text, nm_usuario_p text) AS $body$
DECLARE



/*	Procedure usada para enviar o o pedido de busca da pessoa jurídica na Fila de Transmissão que existe no Gerenciador de Suprimentos.*/

BEGIN

update	pessoa_juridica
set	ie_status_envio	= 'F',
	dt_integracao	= clock_timestamp()
where	cd_cgc		= cd_cnpj_p;

insert into pessoa_jur_fila_transm(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_cgc,
	ie_evento)
values (	nextval('cot_compra_fila_transm_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_cnpj_p,
	'EN');

insert into cot_compra_log_integracao(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_cnpj,
	ie_status_envio,
	ie_tipo_integracao,
	ds_log)
values (	nextval('cot_compra_log_integracao_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_cnpj_p,
	'FP',
	'P',
	WHEB_MENSAGEM_PCK.get_texto(299775));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
