SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE epae_new_record ( nr_seq_pepo_p bigint, nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_atendimento_w		bigint := 0;
nr_cirurgia_w		bigint;
qt_apae_w		bigint;
qt_existe_apae_w		bigint;
cd_procedimento_princ_w	bigint;
ie_origem_proced_w	bigint;
cd_medico_cirurgiao_w	varchar(10);
cd_medico_anestesista_w 	varchar(10);
dt_cirurgia_w		timestamp;
nr_seq_proc_interno_w	bigint;
cd_pessoa_fisica_w	varchar(10);
cd_pf_usuario_w		pessoa_fisica.cd_pessoa_fisica%type := obter_pf_usuario(nm_usuario_p,'C');


BEGIN

if (nr_cirurgia_p > 0) then
	begin
	
	SELECT * FROM apae_obter_dados_cir_externo(nr_cirurgia_p, qt_apae_w, nr_atendimento_w, cd_procedimento_princ_w, ie_origem_proced_w, cd_medico_cirurgiao_w, cd_medico_anestesista_w, dt_cirurgia_w, nr_seq_proc_interno_w) INTO STRICT qt_apae_w, nr_atendimento_w, cd_procedimento_princ_w, ie_origem_proced_w, cd_medico_cirurgiao_w, cd_medico_anestesista_w, dt_cirurgia_w, nr_seq_proc_interno_w;
	
	if (qt_apae_w = 0) then
		begin
		
		select nextval('aval_pre_anestesica_seq')
		into STRICT nr_sequencia_w
		;
		
		select max(cd_pessoa_fisica)
		into STRICT cd_pessoa_fisica_w
		from cirurgia
		where nr_cirurgia = nr_cirurgia_p;
		
		insert into aval_pre_anestesica(
			nr_sequencia,
			nr_cirurgia,
			cd_pessoa_fisica,
			cd_medico,
			cd_avaliador,
			cd_procedimento,
			ie_origem_proced,
			nr_seq_proc_interno,
			dt_cirurgia,
			nm_usuario_nrec,
			nm_usuario,
			dt_atualizacao_nrec,
			dt_atualizacao,
			dt_avaliacao,
			nr_atendimento
		) values (
			nr_sequencia_w,
			nr_cirurgia_p,
			cd_pessoa_fisica_w,
			coalesce(cd_medico_cirurgiao_w,cd_pf_usuario_w),
			coalesce(cd_medico_anestesista_w,cd_pf_usuario_w),
			cd_procedimento_princ_w,
			ie_origem_proced_w,
			nr_seq_proc_interno_w,
			dt_cirurgia_w,
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			clock_timestamp(),
			nr_atendimento_w
		);
		
		end;
	end if;
	
	end;
elsif (nr_seq_pepo_p > 0) then
	begin
	
	SELECT * FROM apae_obter_dados_estrut_pepo(nr_seq_pepo_p, nr_atendimento_w, qt_apae_w, cd_procedimento_princ_w, ie_origem_proced_w, cd_medico_cirurgiao_w, cd_medico_anestesista_w, dt_cirurgia_w) INTO STRICT nr_atendimento_w, qt_apae_w, cd_procedimento_princ_w, ie_origem_proced_w, cd_medico_cirurgiao_w, cd_medico_anestesista_w, dt_cirurgia_w;
								
	if (qt_apae_w = 0) then
		begin
		
		select nextval('aval_pre_anestesica_seq')
		into STRICT nr_sequencia_w
		;
		
		select max(cd_pessoa_fisica)
		into STRICT cd_pessoa_fisica_w
		from cirurgia
		where nr_seq_pepo = nr_seq_pepo_p;
		
		insert into aval_pre_anestesica(
			nr_sequencia,
			nr_seq_pepo,
			cd_pessoa_fisica,
			cd_medico,
			cd_avaliador,
			cd_procedimento,
			ie_origem_proced,
			dt_cirurgia,
			nm_usuario_nrec,
			nm_usuario,
			dt_atualizacao_nrec,
			dt_atualizacao,
			dt_avaliacao,
			nr_atendimento
		) values (
			nr_sequencia_w,
			nr_seq_pepo_p,
			cd_pessoa_fisica_w,
			coalesce(cd_medico_cirurgiao_w,cd_pf_usuario_w),
			coalesce(cd_medico_anestesista_w,cd_pf_usuario_w),
			cd_procedimento_princ_w,
			ie_origem_proced_w,
			dt_cirurgia_w,
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			clock_timestamp(),
			nr_atendimento_w
		);
		
		end;
	end if;
	
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
