SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estender_rep_js ( nr_prescricao_p bigint, ds_lista_atendimento_p text, cd_medico_p text, ie_ajustar_qt_p text, ie_recalc_horario_p bigint, cd_perfil_p bigint, ie_procedimento_p text, ie_agrupamento_p bigint, ie_proced_agora_p text, ie_tipo_pessoa_p text, ie_prim_hor_setor_p text, ie_atend_copia_p text, ie_gerar_liberada_p text, cd_setor_estender_p bigint, nr_atendimento_p bigint, cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE

 
dt_primeiro_w		timestamp;
ds_lista_atend_w	varchar(4000);
nr_pos_virgula_w	bigint;
nr_atendimento_w	bigint;
nr_nova_prescr_w	bigint;
ds_erro_w		varchar(2000);
cd_medic_posicionar_w	bigint;
ie_setar_hor_rep_w		varchar(1);

BEGIN
if (ds_lista_atendimento_p IS NOT NULL AND ds_lista_atendimento_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	select	coalesce(max(ie_setar_hor_rep),'N') 
	into STRICT	ie_setar_hor_rep_w 
	from	setor_atendimento	 
	where	cd_setor_atendimento = cd_setor_estender_p;
	 
	if (ie_prim_hor_setor_p = 'S') then 
		begin 
		dt_primeiro_w	:= obter_prim_hor_setor(cd_setor_estender_p);
		end;
	elsif (ie_prim_hor_setor_p = 'N') or 
		 (ie_prim_hor_setor_p = 'D' AND ie_setar_hor_rep_w = 'N')then 
		begin 
		dt_primeiro_w	:= obter_prim_horario_prescricao(obter_atend_copia_prescr(ie_atend_copia_p,nr_atendimento_p,cd_estabelecimento_p,cd_pessoa_fisica_p),cd_setor_estender_p,clock_timestamp(),nm_usuario_p,'R');
		 
		if (position(':' in dt_primeiro_w) = 0) then 
			dt_primeiro_w := dt_primeiro_w ||' 00:00:00';
		end if;
		end;
	end if;
	 
	ds_lista_atend_w	:= ds_lista_atendimento_p;
	 
	while(ds_lista_atend_w IS NOT NULL AND ds_lista_atend_w::text <> '') loop 
		begin 
		 
		nr_pos_virgula_w	:= position(',' in ds_lista_atend_w);		
		if (nr_pos_virgula_w > 0) then	 
			begin 
			nr_atendimento_w	:= (substr(ds_lista_atend_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_atend_w	:= substr(ds_lista_atend_w,nr_pos_virgula_w+1,length(ds_lista_atend_w));
			end;
		else 
			begin 
			nr_atendimento_w	:= (ds_lista_atend_w)::numeric;
			ds_lista_atend_w	:= null;
			end;
		end if;
		if (coalesce(nr_atendimento_w,0) > 0) then 
			begin 
			 
			SELECT * FROM copia_prescricao( 
				nr_prescricao_p, nr_atendimento_w, nm_usuario_p, cd_medico_p, clock_timestamp(), ie_ajustar_qt_p, ie_procedimento_p, '', ie_agrupamento_p, ie_recalc_horario_p, '', to_char(dt_primeiro_w,'hh24:mi'), clock_timestamp(), '', ie_proced_agora_p, '', '', null, cd_perfil_p, nr_nova_prescr_w, null, null, null, 'N', cd_medic_posicionar_w) INTO STRICT nr_nova_prescr_w, cd_medic_posicionar_w;
				 
			if (ie_gerar_liberada_p = 'S') then 
				begin 
				begin 
				ds_erro_w := liberar_prescricao( 
					nr_nova_prescr_w, nr_atendimento_w, ie_tipo_pessoa_p, cd_perfil_p, nm_usuario_p, 'S', ds_erro_w);
				exception 
					when others then 
					null;
				end;
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
