CREATE OR REPLACE PROCEDURE estender_rep_paciente ( nr_prescricao_p bigint, nr_lista_atendimento_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, gerar_liberada_p text, dt_prim_horario_p text, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_atendimento_w		bigint;
ie_recalcular_horario_w		varchar(1);
recalcularHorariosCopia_w	varchar(1);
cd_medico_w			varchar(10);
nr_lista_atendimento_w		varchar(255);
nr_pos_virgula_w		bigint;
nr_prescricao_nova_w		bigint;
eh_medico_w			varchar(1);
ds_erro_w			varchar(255);
cd_medic_posicionar_w	bigint;


BEGIN 
 
select 	cd_medico, 
	obter_se_medico(cd_medico, 'M') 
into STRICT	cd_medico_w, 
	eh_medico_w 
from	prescr_medica 
where	nr_prescricao	= nr_prescricao_p;
 
recalcularHorariosCopia_w := obter_param_usuario(924, 37, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, recalcularHorariosCopia_w);
 
if (recalcularHorariosCopia_w = 'S') then 
	ie_recalcular_horario_w := 1;
elsif (recalcularHorariosCopia_w = 'I') then 
	ie_recalcular_horario_w := 2;
elsif (recalcularHorariosCopia_w = 'H') then 
	ie_recalcular_horario_w := 3;
elsif (recalcularHorariosCopia_w = 'V') then 
	ie_recalcular_horario_w := 4;
else 
	ie_recalcular_horario_w := 0;
end if;
 
 
if (nr_lista_atendimento_p IS NOT NULL AND nr_lista_atendimento_p::text <> '')	then 
	begin	 
	nr_lista_atendimento_w	:= nr_lista_atendimento_p;
	while(nr_lista_atendimento_w IS NOT NULL AND nr_lista_atendimento_w::text <> '') loop 
		begin 
		nr_pos_virgula_w	:= position(',' in nr_lista_atendimento_w);
		if (nr_pos_virgula_w > 0)	then 
			begin 
			nr_atendimento_w	:= (substr(nr_lista_atendimento_w,1,nr_pos_virgula_w-1))::numeric;
			nr_lista_atendimento_w	:= substr(nr_lista_atendimento_w,nr_pos_virgula_w+1,length(nr_lista_atendimento_w));
			if (nr_atendimento_w > 0) then 
				begin 
				SELECT * FROM Copia_Prescricao(nr_prescricao_p, nr_atendimento_w, nm_usuario_p, cd_medico_w, clock_timestamp(), 'S', 'S', '', 0, ie_recalcular_horario_w, '', dt_prim_horario_p, clock_timestamp(), '', 'N', '', '', null, cd_perfil_p, nr_prescricao_nova_w, null, null, null, 'N', cd_medic_posicionar_w) INTO STRICT nr_prescricao_nova_w, cd_medic_posicionar_w;
				if (gerar_liberada_p = 'S') then 
					 ds_erro_w := Liberar_Prescricao(nr_prescricao_nova_w, nr_atendimento_w, eh_medico_w, cd_perfil_p, nm_usuario_p, 'S', ds_erro_w);
				end if;
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
