CREATE OR REPLACE PROCEDURE estonar_descricao_carta_js ( dt_baixa_p timestamp, ie_baixa_lote_gerado_p text, cd_estabelecimento_p bigint, nm_usuario_p text, cd_tipo_lote_contabil_p bigint, nr_sequencia_p bigint, qt_lotes_p INOUT bigint, ds_erro_p INOUT text) AS $body$
DECLARE
 	

qt_lotes_w	bigint	:= null;
ds_erro_w	varchar(100)	:= '';


BEGIN

if (ie_baixa_lote_gerado_p = 'N') then
	begin
	select   max(nr_lote_contabil)
	into STRICT	 qt_lotes_w
	from     lote_contabil
	where    dt_referencia >= trunc(dt_baixa_p, 'dd')
	and      cd_estabelecimento      = cd_estabelecimento_p
	and      cd_tipo_lote_contabil   = cd_tipo_lote_contabil_p
	and      (dt_geracao_lote IS NOT NULL AND dt_geracao_lote::text <> '');
	end;
else
	begin
	select   max(nr_lote_contabil)
	into STRICT	 qt_lotes_w
	from     lote_contabil
	where    dt_referencia = trunc(dt_baixa_p, 'dd')
	and      cd_estabelecimento      = cd_estabelecimento_p
	and      cd_tipo_lote_contabil   = cd_tipo_lote_contabil_p
	and      (dt_geracao_lote IS NOT NULL AND dt_geracao_lote::text <> '');
	end;		
end if;
	
if	(ie_baixa_lote_gerado_p <> 'S' AND qt_lotes_w IS NOT NULL AND qt_lotes_w::text <> '') then
	begin	
	ds_erro_w := obter_texto_tasy(97042, 1);
	end;
else
	begin
	CALL estornar_movto_cartao_baixa(nr_sequencia_p, nm_usuario_p, 'S', 'S', dt_baixa_p);
	CALL estornar_banco_pend_cartao_cr(nm_usuario_p, nr_sequencia_p, '', clock_timestamp());
	end;
end if;
qt_lotes_p		:=	qt_lotes_w;
ds_erro_p			:=	ds_erro_w;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
