CREATE OR REPLACE PROCEDURE estornar_baixa_cni_conv_receb (nr_seq_receb_p bigint, nr_seq_receb_est_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_baixa_cni_w	bigint;
vl_baixa_w		double precision;
nr_seq_movto_pend_w	bigint;
count_w			bigint;
dt_estorno_w		timestamp;

c01 CURSOR FOR 
SELECT	a.nr_sequencia, 
	a.nr_seq_movto_pend, 
	a.vl_baixa 
from	movto_banco_pend_baixa a 
where	nr_seq_conv_receb	= nr_seq_receb_p;


BEGIN 
 
select	count(*) 
into STRICT	count_w 
from	convenio_ret_receb a 
where	a.nr_seq_receb 		= nr_seq_receb_p;
 
if (count_w > 0) then 
 
	select	coalesce(max(dt_estorno),clock_timestamp()) 
	into STRICT	dt_estorno_w 
	from	convenio_receb_estorno 
	where	nr_sequencia		= nr_seq_receb_est_p;
 
	open C01;
	loop 
	fetch C01 into 
		nr_seq_baixa_cni_w, 
		nr_seq_movto_pend_w, 
		vl_baixa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
 
		CALL baixar_movto_banco_pend( 
				nr_seq_movto_pend_w, 
				dt_estorno_w, 
				vl_baixa_w * -1, 
				null, 
				null, 
				nm_usuario_p, 
				'S', 
				null, 
				nr_seq_receb_p, 
				null, 
				null, 
				null, 
				null, 
				null, 
				null);
 
		end;
	end loop;
	close C01;
end if;
 
end;
$body$
