SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_baixa_oc_nota_dev ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, dt_prevista_entrega_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, qt_devolucao_p bigint) AS $body$
DECLARE


qt_existe_w				bigint;
nr_nota_fiscal_w			varchar(255);
ds_lista_nota_w				varchar(4000) := '';
ds_mensagem_w				varchar(4000) := '';

c01 CURSOR FOR
SELECT	distinct
	nr_nota_fiscal
from	ordem_compra_nota_fiscal
where	nr_ordem_compra	= nr_ordem_compra_p
and	nr_item_oci	= nr_item_oci_p;


BEGIN

open C01;
loop
fetch C01 into	
	nr_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_lista_nota_w := substr(ds_lista_nota_w || nr_nota_fiscal_w || ', ',1,4000);
	end;
end loop;
close C01;

if (coalesce(qt_devolucao_p,0) <> 0) then

	update	ordem_compra_item
	set	qt_material_entregue	= (qt_material_entregue - qt_devolucao_p),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_ordem_compra		= nr_ordem_compra_p
	and	nr_item_oci		= nr_item_oci_p;

	update	ordem_compra_item_entrega
	set	dt_real_entrega		 = NULL,
		qt_real_entrega		= (qt_real_entrega - qt_devolucao_p),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_ordem_compra		= nr_ordem_compra_p
	and	nr_item_oci		= nr_item_oci_p
	and	trunc(dt_prevista_entrega,'dd')	= trunc(dt_prevista_entrega_p,'dd')
	and	coalesce(dt_cancelamento::text, '') = '';

	update	ordem_compra
	set	dt_baixa	 = NULL
	where	nr_ordem_compra	= nr_ordem_compra_p;


	ds_mensagem_w := WHEB_MENSAGEM_PCK.get_texto(300297,'NR_ITEM_OCI_P=' || nr_item_oci_p);

	if (ds_lista_nota_w IS NOT NULL AND ds_lista_nota_w::text <> '') then
		ds_mensagem_w := ds_mensagem_w || chr(13) || chr(10) || WHEB_MENSAGEM_PCK.get_texto(300300,'DS_LISTA_NOTA_W=' || substr(ds_lista_nota_w,1,length(ds_lista_nota_w) -2));
	end if;

	CALL inserir_historico_ordem_compra(
		nr_ordem_compra_p,
		'S',
		WHEB_MENSAGEM_PCK.get_texto(300295),
		ds_mensagem_w,
		nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
