CREATE OR REPLACE PROCEDURE estornar_calc_cot_compra ( nr_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_aprovacao_w		bigint;

c01 CURSOR FOR
SELECT	distinct nr_seq_aprovacao
from	cot_compra_item
where	nr_cot_compra	= nr_cot_compra_p;


BEGIN

update	cot_compra_item
set	dt_aprovacao		 = NULL,
	nm_usuario_aprov		 = NULL,
	dt_reprovacao		 = NULL
where	nr_cot_compra		= nr_cot_compra_p;

open c01;
loop
fetch c01 into
	nr_seq_aprovacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update	cot_compra_item
	set	nr_seq_aprovacao		 = NULL,
		dt_aprovacao		 = NULL,
		dt_reprovacao		 = NULL,
		nm_usuario_aprov		 = NULL
	where	nr_seq_aprovacao		= nr_seq_aprovacao_w;

	delete from processo_aprov_compra
	where	nr_sequencia 	= nr_seq_aprovacao_w;

end loop;
close c01;

update	cot_compra
set	dt_calculo_cotacao		 = NULL,
	dt_aprovacao		 = NULL,
	nm_usuario_aprov		 = NULL,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_cot_compra		= nr_cot_compra_p;

insert into cot_compra_hist(
	nr_sequencia,
	nr_cot_compra,
	dt_atualizacao,
	nm_usuario,
	dt_historico,
	ds_titulo,
	ds_historico,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ie_origem,
	ie_tipo,
	dt_liberacao,
	nm_usuario_lib)
values (	nextval('cot_compra_hist_seq'),
	nr_cot_compra_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	WHEB_MENSAGEM_PCK.get_texto(300321),
	WHEB_MENSAGEM_PCK.get_texto(300323),
	clock_timestamp(),
	nm_usuario_p,
	'S',
	'H',
	clock_timestamp(),
	nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
