CREATE OR REPLACE PROCEDURE estornar_carta_correcao ( nr_seq_carta_p bigint, nr_sequencia_p bigint default null, dt_liberacao_p text default null) AS $body$
BEGIN

if ((coalesce(nr_sequencia_p::text, '') = '') and (coalesce(dt_liberacao_p::text, '') = '')) then
	update	fis_carta_correcao
	set	dt_liberacao		 = NULL,
		nm_usuario_liberacao	 = NULL
	where	nr_sequencia		= nr_seq_carta_p;
else
	update	fis_carta_correcao
	set	dt_liberacao		 = NULL,
		nm_usuario_liberacao	 = NULL
	where	nr_seq_nota_fiscal		= nr_sequencia_p
	and	to_char(dt_liberacao,'dd/mm/yyyy')	= dt_liberacao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
