SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_consistir_retorno (nr_seq_retorno_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_audit_w	bigint;


BEGIN

select	max(nr_seq_lote_audit)
into STRICT	nr_seq_lote_audit_w
from	convenio_retorno_item c,
	conta_paciente_ret_hist b,
	lote_audit_tit_rec a
where	a.nr_sequencia		= b.nr_seq_lote_audit
and	b.nr_seq_ret_item	= c.nr_sequencia
and	c.nr_seq_retorno	= nr_seq_retorno_p
and	(a.dt_baixa IS NOT NULL AND a.dt_baixa::text <> '');

if (nr_seq_lote_audit_w IS NOT NULL AND nr_seq_lote_audit_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265626,'chr(13)='||chr(13)|| 'seq_lote_audit= '||nr_seq_lote_audit_w);
	--Não é possível estornar a consistência deste retorno.' || chr(13) ||
	--As guias deste retorno já estão no lote de títulos ' || nr_seq_lote_audit_w || ' da auditoria, que já foi baixado!
end if;

update	convenio_retorno
set	dt_consistencia	 = NULL,
	ie_status_retorno	= 'R',
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia	= nr_seq_retorno_p;

delete	from	convenio_retorno_venc
where	nr_seq_retorno	= nr_seq_retorno_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
