CREATE OR REPLACE PROCEDURE estornar_data_envio_lote_unit ( nr_solic_unitarizacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


--SOLIC_UNITARIZACAO
cd_local_origem_w	solic_unitarizacao.cd_local_estoque_origem%type;
cd_local_destino_w	solic_unitarizacao.cd_local_estoque_destino%type;

ds_erro_w		varchar(4000);
cd_operacao_estoque_w	smallint;


BEGIN
select	cd_local_estoque_origem,
	cd_local_estoque_destino
into STRICT	cd_local_origem_w,
	cd_local_destino_w
from	solic_unitarizacao
where	nr_sequencia = nr_solic_unitarizacao_p;

cd_operacao_estoque_w := sup_obter_operacao_unit('ST');

if (cd_local_origem_w <> cd_local_destino_w) then
	CALL gerar_movto_estoque_trans_unit(nr_solic_unitarizacao_p, cd_operacao_estoque_w, '2', 'O', nm_usuario_p);
end if;

update	solic_unitarizacao
set	dt_envio  = NULL,
	cd_pessoa_envio  = NULL,
	cd_pessoa_transporte  = NULL,
	dt_transporte  = NULL,
	cd_local_estoque_origem  = NULL,
	cd_local_estoque_destino  = NULL,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_sequencia = nr_solic_unitarizacao_p;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
