SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_entrega_item_oc ( nr_sequencia_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_ordem_compra_w		bigint;
cd_material_w			integer;
ds_material_w			varchar(255);
ds_historico_w			varchar(4000);


BEGIN

update	ordem_compra_item_entrega
set	dt_confirma_entrega  = NULL,
	nm_usuario_conf_entrega  = NULL
where	nr_sequencia = nr_sequencia_p;

select	a.nr_ordem_compra,
	a.cd_material,
	substr(obter_desc_material(a.cd_material),1,255) ds_material
into STRICT	nr_ordem_compra_w,
	cd_material_w,
	ds_material_w
from	ordem_compra_item a,
	ordem_compra_item_entrega b
where	a.nr_ordem_compra = b.nr_ordem_compra
and	a.nr_item_oci = b.nr_item_oci
and	b.nr_sequencia = nr_sequencia_p;

ds_historico_w	:= substr(WHEB_MENSAGEM_PCK.get_texto(331498,	'CD_MATERIAL_W='||cd_material_w||
								';DS_MATERIAL_W='||ds_material_w||
								';DT_HISTORICO_W='||clock_timestamp()),1,4000);

if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then
	ds_historico_w := substr(ds_historico_w || chr(13) || chr(10) || chr(13) || chr(10) || WHEB_MENSAGEM_PCK.get_texto(298449,'DS_OBSERVACAO_P='||ds_observacao_p),1,4000);
end if;

CALL inserir_historico_ordem_compra(
	nr_ordem_compra_w,
	'S',
	WHEB_MENSAGEM_PCK.get_texto(331500),
	ds_historico_w,
	nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
