CREATE OR REPLACE PROCEDURE estornar_geracao_lote_cobranca ( nr_seq_lote_p text, nm_usuario_p text) AS $body$
DECLARE


ds_historico_w		varchar(255);
dt_aprovacao_w		timestamp;


BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '')then

	update lote_orgao_cobranca
	set ie_situacao_lote = 'P',
		dt_remessa_retorno  = NULL,
		ds_arquivo  = NULL
	where nr_sequencia = nr_seq_lote_p;

	ds_historico_w := wheb_mensagem_pck.get_texto(337822, 'NM_USUARIO=' || nm_usuario_p || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
	CALL gerar_historico_cobr_orgao(null,null,nr_seq_lote_p,ds_historico_w,nm_usuario_p,'E');

end if;

commit;

end;
$body$
