SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_liberacao_devolucao ( nr_devolucao_p bigint, ds_consistencia_p INOUT text) AS $body$
DECLARE


qt_item_w		smallint;
ds_consistencia_w	varchar(255);


BEGIN
qt_item_w		:= 0;

select	count(*)
into STRICT	qt_item_w
from	item_devolucao_material_pac
where	nr_devolucao = nr_devolucao_p
and	(dt_recebimento IS NOT NULL AND dt_recebimento::text <> '');

if (qt_item_w > 0) then
	ds_consistencia_w	:= obter_desc_expressao(616248); --'Existem materiais já recebidos, impossível Estornar';
else
	update devolucao_material_pac
	set	 dt_liberacao_baixa  = NULL
	where	 nr_devolucao = nr_devolucao_p;
end if;

if (ds_consistencia_w IS NOT NULL AND ds_consistencia_w::text <> '') then
	ds_consistencia_p	:= substr(ds_consistencia_w,1,254);
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
