SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_lib_autor_carga_estoq ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	sup_autor_carga_est_hist
where	nr_seq_autor = nr_sequencia_p
and	ie_origem in ('G','C','S','X','I');

if (qt_existe_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265981);
	--'Essa autorização já foi utilizada para efetuar alguma ação na carga de arquivos, portanto não pode mais estornar a  liberação da mesma.'
end if;

update	sup_autor_carga_estoque
set	dt_liberacao	 = NULL,
	nm_usuario_lib	= ''
where	nr_sequencia	= nr_sequencia_p;

CALL gerar_hist_autor_carga_estoque(
	nr_sequencia_p,
	'E',
	wheb_mensagem_pck.get_Texto(313408), /*'Estorno da liberação da autorização',*/
	wheb_mensagem_pck.get_Texto(313409), /*'Foi estornado a liberação da autorização para carga de estoque.',*/
	nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
