SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_lib_devolucao ( nr_devolucao_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_baixa_total_w			timestamp;


BEGIN

select	dt_baixa_total
into STRICT	dt_baixa_total_w
from	devolucao_material_pac
where	nr_devolucao	= nr_devolucao_p;

if (dt_baixa_total_w IS NOT NULL AND dt_baixa_total_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265982);
	--'Não é possível estornar a liberação de uma devolução já baixada!'
end if;

if (coalesce(dt_baixa_total_w::text, '') = '') then
	update	devolucao_material_pac
	set	dt_liberacao_baixa	 = NULL,
		nm_usuario		= nm_usuario_p
	where	nr_devolucao		= nr_devolucao_p;
	commit;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
