SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_lib_solic_compra ( nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
cd_estabelecimento_w		smallint;
ie_estorna_aprov_solic_w	varchar(1);
nr_seq_aprovacao_w		bigint;
ie_empenho_orcamento_w		varchar(10);

c01 CURSOR FOR 
SELECT	distinct nr_seq_aprovacao 
from	solic_compra_item 
where	nr_solic_compra	= nr_solic_compra_p;


BEGIN 
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	solic_compra 
where	nr_solic_compra	= nr_solic_compra_p;
 
select	coalesce(max(ie_empenho_orcamento),'N') 
into STRICT	ie_empenho_orcamento_w 
from	parametro_estoque 
where	cd_estabelecimento	= cd_estabelecimento_w;
 
select	coalesce(max(ie_estorna_aprov_solic), 'S') 
into STRICT	ie_estorna_aprov_solic_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_w;
 
 
update	solic_compra 
set	dt_liberacao		 = NULL, 
	dt_pre_liberacao	 = NULL, 
	nm_usuario_lib		= '', 
	nm_usuario_pre_lib	= '', 
	dt_autorizacao		 = NULL 
where	nr_solic_compra	= nr_solic_compra_p;
 
delete from reg_lic_item_solic 
where nr_solic_compra = nr_solic_compra_p;
 
if (ie_estorna_aprov_solic_w = 'S') then 
 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_aprovacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		delete 
		from	processo_aprov_compra 
		where	nr_sequencia 	= nr_seq_aprovacao_w;
		 
		end;
	end loop;
	close C01;
 
	update	solic_compra_item 
	set	nr_seq_aprovacao	 = NULL, 
		dt_autorizacao	 = NULL, 
		dt_reprovacao	 = NULL 
	where	nr_solic_compra = nr_solic_compra_p;
 
end if;
 
if (ie_empenho_orcamento_w = 'S') then 
	CALL gerar_empenho_solic_compra(nr_solic_compra_p,nm_usuario_p);
end if;
 
 
CALL gerar_historico_solic_compra( 
			nr_solic_compra_p, 
			Wheb_mensagem_pck.get_Texto(300829), /*'Estorno da liberação da solicitação de compras',*/
 
			WHEB_MENSAGEM_PCK.get_texto(300830,'DT_LIBERACAO_W='|| to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss') ||';NM_USUARIO_P='|| NM_USUARIO_P ),/*'Foi estornado a liberação da solicitação no dia ' || to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss') || ' pelo usuário ' || nm_usuario_p || '.',*/ 
			'R', 
			nm_usuario_p);
 
CALL sup_cancela_email_pendente(null,nr_solic_compra_p,'SC',cd_estabelecimento_w,nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE estornar_lib_solic_compra ( nr_solic_compra_p bigint, nm_usuario_p text) FROM PUBLIC;

