CREATE OR REPLACE PROCEDURE estornar_lote_encontro_contas ( nr_seq_lote_p bigint, dt_baixa_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_pessoa_w		bigint;						
						 
C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	pessoa_encontro_contas	a 
	where	a.nr_seq_lote		= nr_seq_lote_p 
	and 	(dt_baixa IS NOT NULL AND dt_baixa::text <> '');
	

BEGIN 
 
dbms_application_info.SET_ACTION('ESTORNAR_LOTE_ENCONTRO_CONTAS');
 
open C01;
loop 
fetch C01 into	 
	nr_seq_pessoa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL estornar_pessoa_enc_contas(nr_seq_pessoa_w,dt_baixa_p,'N',nm_usuario_p);
	end;
end loop;
close C01;
 
update	lote_encontro_contas 
set	dt_baixa 	 = NULL 
where	nr_sequencia 	= nr_seq_lote_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
