SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_lote_producao ( nr_lote_producao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_contab_w				smallint;
cd_material_w			material.cd_material%type;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
ie_estoque_lote_w		material_estab.ie_estoque_lote%type;
nr_lote_fornec_w		material_lote_fornec.nr_sequencia%type;
ds_erro_w				varchar(255) := '';


BEGIN
select	count(*)
into STRICT	qt_contab_w
from	lote_producao
where	nr_lote_producao	= nr_lote_producao_p
and	coalesce(nr_lote_contabil, 0) <> 0;
if (qt_contab_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265983);
	--'Este lote de producao ja esta contabilizado, nao pode ser estornado'
end if;

select	cd_material,
	cd_estabelecimento
into STRICT	cd_material_w,
	cd_estabelecimento_w
from	lote_producao
where	nr_lote_producao = nr_lote_producao_p;

ie_estoque_lote_w := obter_se_material_estoque_lote(cd_estabelecimento_w,cd_material_w);

update	lote_producao
set	dt_confirmacao	 = NULL,
	nm_usuario	= nm_usuario_p
where	nr_lote_producao	= nr_lote_producao_p;

if (ie_estoque_lote_w = 'S') then
	select	max(nr_sequencia)
	into STRICT	nr_lote_fornec_w
	from	material_lote_fornec
	where	nr_lote_producao = nr_lote_producao_p;
	
	ds_erro_w := consistir_excluir_lote_fornec(nr_lote_fornec_w, ds_erro_w);
	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(ds_erro_w);
	end if;
end if;

CALL GERAR_MOVTO_ESTOQUE_PRODUCAO(
			nr_lote_producao_p,
			nm_usuario_p,
			'2');

if (ie_estoque_lote_w = 'S') then
	CALL delete_material_lote_fornec(nr_lote_fornec_w);
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
