SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_movimento_estoque ( nr_movimento_estoque_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_movimento_estoque_w		bigint;
nr_movimento_corresp_w		bigint:= null;


BEGIN
select	nr_movimento_estoque_corresp
into STRICT	nr_movimento_corresp_w
from	movimento_estoque
where	nr_movimento_estoque = nr_movimento_estoque_p;
/* matheus os 50419 */

if (nr_movimento_corresp_w IS NOT NULL AND nr_movimento_corresp_w::text <> '') and (nr_movimento_corresp_w > nr_movimento_estoque_p) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265984);
	--' Este movimento de estoque já foi estornado!'
end if;

select	nextval('movimento_estoque_seq')
into STRICT	nr_movimento_estoque_w
;

insert	into movimento_estoque(
	nr_movimento_estoque,
	cd_estabelecimento,
	cd_local_estoque,
	dt_movimento_estoque,
	cd_operacao_estoque,
	cd_acao,
	cd_material,
	dt_mesano_referencia,
	qt_movimento,
	dt_atualizacao,
	nm_usuario,
	ie_origem_documento,
	nr_documento,
	nr_sequencia_item_docto,
	cd_cgc_emitente,
	cd_serie_nf,
	nr_sequencia_documento,
	vl_movimento,
	cd_unidade_medida_estoque,
	cd_procedimento,
	cd_setor_atendimento,
	cd_conta,
	dt_contabil,
	cd_lote_fabricacao,
	dt_validade,
	qt_estoque,
	cd_local_estoque_destino,
	cd_centro_custo,
	cd_unidade_med_mov,
	nr_movimento_estoque_corresp,
	cd_conta_contabil,
	ie_origem_proced,
	cd_fornecedor,
	nr_lote_contabil,
	qt_inventario,
	ds_observacao,
	nr_seq_tab_orig,
	nr_seq_lote_fornec,
	cd_funcao,
	cd_perfil,
	nr_prescricao,
	nr_atendimento)
SELECT	nr_movimento_estoque_w,
	cd_estabelecimento,
	cd_local_estoque,
	dt_movimento_estoque,
	cd_operacao_estoque,
	CASE WHEN cd_acao=1 THEN  '2'  ELSE '1' END ,
	cd_material,
	dt_mesano_referencia,
	qt_movimento,
	clock_timestamp(),
	nm_usuario_p,
	ie_origem_documento,
	nr_documento,
	nr_sequencia_item_docto,
	cd_cgc_emitente,
	cd_serie_nf,
	nr_sequencia_documento,
	vl_movimento,
	cd_unidade_medida_estoque,
	cd_procedimento,
	cd_setor_atendimento,
	cd_conta,
	dt_contabil,
	cd_lote_fabricacao,
	dt_validade,
	qt_estoque,
	cd_local_estoque_destino,
	cd_centro_custo,
	cd_unidade_med_mov,
	nr_movimento_estoque_p,
	cd_conta_contabil,
	ie_origem_proced,
	cd_fornecedor,
	nr_lote_contabil,
	qt_inventario,
	substr(wheb_mensagem_pck.get_Texto(313410, 'DS_OBSERVACAO_W='|| DS_OBSERVACAO),1,255), /*Estornado na administração estoque. #@DS_OBSERVACAO_W#@*/
	nr_seq_tab_orig,
	nr_seq_lote_fornec,
	cd_funcao,
	cd_perfil,
	nr_prescricao,
	nr_atendimento
from	movimento_estoque
where	nr_movimento_estoque = nr_movimento_estoque_p;

update	movimento_estoque
set	nr_movimento_estoque_corresp = nr_movimento_estoque_w
where	nr_movimento_estoque = nr_movimento_estoque_p;

insert	into movimento_estoque_valor
SELECT	nr_movimento_estoque_w,
	cd_tipo_valor,
	vl_movimento,
	clock_timestamp(),
	nm_usuario_p
from	movimento_estoque_valor
where	nr_movimento_estoque = nr_movimento_estoque_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
