SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_movto_trans_cheque ( nr_seq_cheque_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_transacao_w		smallint;
nr_seq_movto_w		movto_trans_financ.nr_sequencia%type;
nr_seq_movto_estorno_w	movto_trans_financ.nr_sequencia%type;
ds_sqlerrm_w		varchar(4000);


BEGIN

select	coalesce(sign(sum(a.vl_transacao)),0),
	max(a.nr_sequencia)
into STRICT	vl_transacao_w,
	nr_seq_movto_w
from	transacao_financeira b,
	movto_trans_financ a
where	b.ie_acao = 7
and	a.nr_seq_trans_financ = b.nr_sequencia
and	a.nr_seq_cheque = nr_seq_cheque_p;

if (vl_transacao_w <> 0) then
	begin
	nr_seq_movto_estorno_w := estornar_movto_trans_financ(nr_seq_movto_w, nm_usuario_p, nr_seq_movto_estorno_w);
	exception
	when others then
		ds_sqlerrm_w	:= substr(sqlerrm,1,4000);
		/*Ocorreu uma inconsistência ao estornar a movimentação bancária do cheque!
		#@DS_SQLERRM#@*/
		CALL wheb_mensagem_pck.exibir_mensagem_abort(282938,'DS_SQLERRM='||ds_sqlerrm_w);
	end;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
