SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_pessoa_enc_contas ( nr_seq_pessoa_p bigint, dt_baixa_p timestamp, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_baixa_w			bigint;
nr_seq_lote_w			bigint;
nr_tit_pag_w			bigint;
nr_tit_rec_w			bigint;
nr_titulo_w			bigint;
ie_receber_pagar_w		varchar(1);
nr_seq_item_mensalidade_w	pls_mensalidade_seg_item.nr_sequencia%type;	
nr_seq_seg_mens_w		pls_segurado_mensalidade.nr_sequencia%type;

C01 CURSOR FOR 
	SELECT	'R' ie_receber_pagar, 
		a.nr_titulo_receber nr_titulo 
	from	titulo_receber b, 
		encontro_contas_item a 
	where	a.nr_seq_pessoa		= nr_seq_pessoa_p 
	and	a.nr_titulo_receber	= b.nr_titulo 
	
union all
 
	SELECT	'P' ie_receber_pagar, 
		a.nr_titulo_pagar nr_titulo 
	from	titulo_pagar b, 
		encontro_contas_item a 
	where	a.nr_seq_pessoa		= nr_seq_pessoa_p 
	and	a.nr_titulo_pagar	= b.nr_titulo;

C02 CURSOR FOR 
	SELECT	nr_seq_item_mensalidade, 
		nr_sequencia 
	from	pls_segurado_mensalidade 
	where	nr_seq_pessoa_enc_contas = nr_seq_pessoa_p;


BEGIN 
select	nr_seq_lote, 
	nr_titulo_receber, 
	nr_titulo_pagar 
into STRICT	nr_seq_lote_w, 
	nr_tit_rec_w, 
	nr_tit_pag_w 
from	pessoa_encontro_contas 
where	nr_sequencia = nr_seq_pessoa_p;
 
open C02;
loop 
fetch C02 into 
	nr_seq_item_mensalidade_w, 
	nr_seq_seg_mens_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
	if (nr_seq_item_mensalidade_w IS NOT NULL AND nr_seq_item_mensalidade_w::text <> '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(355300, null);
	else 
		if (nr_tit_rec_w IS NOT NULL AND nr_tit_rec_w::text <> '') then 
			update	titulo_receber 
			set	nr_seq_segurado_mens  = NULL 
			where	nr_seq_segurado_mens = nr_seq_seg_mens_w;
		elsif (nr_tit_pag_w IS NOT NULL AND nr_tit_pag_w::text <> '') then 
			update	titulo_pagar 
			set	nr_seq_segurado_mens  = NULL 
			where	nr_seq_segurado_mens = nr_seq_seg_mens_w;
		end if;
		 
		delete	from pls_segurado_mensalidade 
		where	nr_sequencia = nr_seq_seg_mens_w;
	end if;
	end;
end loop;
close C02;
 
open C01;
loop 
fetch C01 into 
	ie_receber_pagar_w, 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (ie_receber_pagar_w = 'P') then 
		select	max(a.nr_sequencia) 
		into STRICT	nr_seq_baixa_w 
		from	titulo_pagar_baixa a 
		where	a.nr_titulo = nr_titulo_w 
		and 	a.nr_seq_lote_enc_contas = nr_seq_lote_w;	
		 
		if (nr_seq_baixa_w IS NOT NULL AND nr_seq_baixa_w::text <> '') then 
			CALL estornar_tit_pagar_baixa(nr_titulo_w,nr_seq_baixa_w,dt_baixa_p,nm_usuario_p,'N');
		end if;
	elsif (ie_receber_pagar_w = 'R') then 
		select	max(a.nr_sequencia) 
		into STRICT	nr_seq_baixa_w 
		from	titulo_receber_liq a 
		where	a.nr_titulo = nr_titulo_w 
		and 	a.nr_seq_lote_enc_contas = nr_seq_lote_w;
		 
		if (nr_seq_baixa_w IS NOT NULL AND nr_seq_baixa_w::text <> '') then 
		dbms_application_info.SET_ACTION('ESTORNAR_PESSOA_ENC_CONTAS');
		CALL estornar_tit_receber_liq(nr_titulo_w,nr_seq_baixa_w,dt_baixa_p,nm_usuario_p,'N', 
					wheb_mensagem_pck.get_texto(304049, 'NR_SEQ_LOTE_W=' || nr_seq_lote_w),null); -- Baixa estornada pelo lote de encontro de contas #@NR_SEQ_LOTE_W#@. 
		end if;			
	end if;
	end;
end loop;
close C01;
 
if (nr_tit_pag_w IS NOT NULL AND nr_tit_pag_w::text <> '') then 
	CALL cancelar_titulo_pagar(nr_tit_pag_w,nm_usuario_p,dt_baixa_p);
elsif (nr_tit_rec_w IS NOT NULL AND nr_tit_rec_w::text <> '') then 
	CALL cancelar_titulo_receber(nr_tit_rec_w,nm_usuario_p,'N',dt_baixa_p);
end if;
 
update	pessoa_encontro_contas 
set	nr_titulo_receber  = NULL, 
	nr_titulo_pagar	  = NULL, 
	dt_baixa 	  = NULL 
where	nr_sequencia 	 = nr_seq_pessoa_p;
 
update	lote_encontro_contas 
set	dt_baixa 	 = NULL 
where	nr_sequencia 	= nr_seq_lote_w;
 
CALL atualizar_vl_encontro_contas( nr_seq_pessoa_p, nr_seq_lote_w, nm_usuario_p);
 
if (ie_commit_p = 'S') then 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE estornar_pessoa_enc_contas ( nr_seq_pessoa_p bigint, dt_baixa_p timestamp, ie_commit_p text, nm_usuario_p text) FROM PUBLIC;

