CREATE OR REPLACE PROCEDURE estornar_pre_aprovacao_compras ( nr_solic_compra_p bigint) AS $body$
DECLARE

 
dt_aprovacao_w			timestamp;


BEGIN 
 
select	dt_autorizacao 
into STRICT	dt_aprovacao_w 
from	solic_compra 
where	nr_solic_compra = nr_solic_compra_p;
 
if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265985);
	--'Essta solicitação já está aprovada.'); 
end if;
 
update	solic_compra 
set	dt_pre_aprovacao 	= '' 
where	nr_solic_compra	= nr_solic_compra_p;
 
commit;
 
end;
