CREATE OR REPLACE PROCEDURE estornar_proc_encaminhado ( nr_seq_aprovacao_p bigint, nr_seq_processo_p bigint) AS $body$
DECLARE


nr_seq_processo_w			bigint;
dt_liberacao_w			timestamp;
cd_cargo_w			bigint;
ie_aprov_reprov_w			varchar(1);


BEGIN

select	cd_cargo
into STRICT	cd_cargo_w
from	processo_aprov_compra
where	nr_sequencia		= nr_seq_aprovacao_p
and	nr_seq_proc_aprov		= nr_seq_processo_p;

select	coalesce(max(ie_aprov_reprov), 'X')
into STRICT	ie_aprov_reprov_w
from	processo_aprov_compra
where	nr_sequencia		= nr_seq_aprovacao_p
and	nr_seq_proc_aprov		<> nr_seq_processo_p
and	cd_cargo_enc		= cd_cargo_w;
if (ie_aprov_reprov_w <> 'P') then
	--(-20011, 'O Processo encaminhado não pode ser estornado, pois não está está mais pendente.');
	CALL wheb_mensagem_pck.exibir_mensagem_abort(177693);
end if;


update	processo_aprov_compra
set	dt_definicao		= clock_timestamp(),
	ie_aprov_reprov		= 'P'
where	nr_sequencia		= nr_seq_aprovacao_p
and	nr_seq_proc_aprov		= nr_seq_processo_p;

delete
from	processo_aprov_compra
where	nr_sequencia		= nr_seq_aprovacao_p
and	nr_seq_proc_aprov		<> nr_seq_processo_p
and	cd_cargo_enc		= cd_cargo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
