SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estornar_proc_vacina ( nr_atendimento_p bigint, nr_seq_vacina_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_gerada_w		procedimento_paciente.nr_sequencia%type;
nr_sequencia_w		procedimento_paciente.nr_sequencia%type;



BEGIN

if (coalesce(nr_atendimento_p,0) > 0) and (coalesce(nr_seq_vacina_p,0) > 0) then

	select	max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	FROM	procedimento_paciente a,
		paciente_vacina b
	WHERE	a.nr_atendimento	= nr_atendimento_p
	AND	a.nr_atendimento	= b.nr_atendimento
	AND	b.nr_sequencia		= nr_seq_vacina_p
	AND	a.cd_procedimento	= b.cd_procedimento;

	nr_seq_gerada_w := duplicar_proc_paciente(nr_sequencia_w, nm_usuario_p, nr_seq_gerada_w);

	if (nr_seq_gerada_w IS NOT NULL AND nr_seq_gerada_w::text <> '') then

		update 	procedimento_paciente
		set	--nr_interno_conta 	= null,
			qt_procedimento	= qt_procedimento * -1,
			vl_procedimento = vl_procedimento * -1,
			vl_medico = vl_medico * -1,
			vl_anestesista = vl_anestesista * -1,
			vl_materiais = vl_materiais * -1,
			vl_auxiliares = vl_auxiliares * -1,
			vl_custo_operacional = vl_custo_operacional * -1,
			vl_adic_plant = vl_adic_plant * -1,
			ds_observacao	= Wheb_mensagem_pck.get_Texto(1091073, 'DS_OBSERVACAO_W='|| ds_observacao), /*ds_observacao || ' Estorno gerado a partir da suspens√£o da vacina.'*/
			dt_procedimento	= clock_timestamp()
		where nr_sequencia = nr_seq_gerada_w;

		update 	procedimento_paciente
		set	nr_seq_proc_est	= nr_seq_gerada_w
		where nr_sequencia	= nr_sequencia_w;

		commit;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
