CREATE OR REPLACE PROCEDURE estornar_receb_roupa ( nr_sequencia_p bigint, nm_usuario_p text, nr_seq_inventario_p bigint) AS $body$
DECLARE



qt_pendente_w  bigint;


BEGIN



update  rop_inv_reposicao_item
set  dt_recebimento = '',
 nm_usuario_receb = ''
where nr_sequencia = nr_sequencia_p;

select  count(*)
into STRICT    qt_pendente_w
from    rop_inv_reposicao_item
where   nr_seq_inventario = nr_seq_inventario_p
and     nr_sequencia = nr_sequencia_p
and (dt_recebimento IS NOT NULL AND dt_recebimento::text <> '');


if (qt_pendente_w = 0) then
 update  rop_inv_reposicao
 set  dt_recebimento = '',
  nm_usuario_receb = ''
 where nr_sequencia = nr_seq_inventario_p;
end if;


commit;

end;
$body$
