CREATE OR REPLACE PROCEDURE estorna_baixa_solic_cot_compra ( nr_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_solic_compra_w				bigint;
nr_item_solic_compra_w				bigint;

c01 CURSOR FOR 
SELECT	distinct 
	nr_solic_compra, 
	nr_item_solic_compra 
from (	SELECT	a.nr_solic_compra, 
		a.nr_item_solic_compra 
	from	cot_compra_item a 
	where	a.nr_cot_compra = nr_cot_compra_p 
	and	(a.nr_solic_compra IS NOT NULL AND a.nr_solic_compra::text <> '') 
	
union
 
	select	b.nr_solic_compra, 
		b.nr_item_solic_compra 
	from	cot_compra_item a, 
		cot_compra_solic_agrup b 
	where	a.nr_cot_compra = b.nr_cot_compra 
	and	a.nr_item_cot_compra = b.nr_item_cot_compra 
	and	a.nr_cot_compra = nr_cot_compra_p 
	and	(b.nr_solic_compra IS NOT NULL AND b.nr_solic_compra::text <> '')) alias2;
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_solic_compra_w, 
	nr_item_solic_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	update	solic_compra 
	set	dt_baixa = '' 
	where	nr_solic_compra	= nr_solic_compra_w 
	and	coalesce(nr_seq_motivo_cancel::text, '') = '' 
	and	(dt_baixa IS NOT NULL AND dt_baixa::text <> '');
	 
	update	solic_compra_item 
	set	dt_baixa = '' 
	where	nr_solic_compra	= nr_solic_compra_w 
	and	nr_item_solic_compra = nr_item_solic_compra_w 
	and	coalesce(nr_seq_motivo_cancel::text, '') = '' 
	and	(dt_baixa IS NOT NULL AND dt_baixa::text <> '');	
	 
	 
	CALL gerar_historico_solic_compra( 
			nr_solic_compra_w, 
			wheb_mensagem_pck.get_texto(300287), 
			wheb_mensagem_pck.get_texto(300289, 'NR_COT_COMPRA=' || nr_cot_compra_p), 
			'T', 
			nm_usuario_p);
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
