SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estorna_cancel_processo_compra ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nm_usuario_p text, qt_material_cancelado_p bigint default 0) AS $body$
DECLARE


nr_cot_compra_w			bigint;
nr_item_cot_compra_w		bigint;
nr_solic_compra_w			bigint;
nr_item_solic_compra_w		bigint;
ds_observacao_w			varchar(4000);
ds_obs_estorno_w			varchar(1000);
ds_observacao_orig_w		varchar(255);
nr_seq_motivo_cancel_w		bigint;
cd_motivo_baixa_w			bigint;

qt_material_cancelado_w		solic_compra_item.qt_material_cancelado%type;


BEGIN

select	coalesce(max(nr_cot_compra),0)
into STRICT	nr_cot_compra_w
from  	ordem_compra_item
where	nr_ordem_Compra = nr_ordem_compra_p;

if (nr_cot_compra_w > 0) then

	ds_obs_estorno_w	:= wheb_mensagem_pck.get_texto(300296);

	select	coalesce(max(ds_observacao),''),
		max(nr_seq_motivo_cancel)
	into STRICT	ds_observacao_w,
		nr_seq_motivo_cancel_w
	from	cot_compra
	where	nr_cot_compra = nr_cot_compra_w;

	if (nr_seq_motivo_cancel_w IS NOT NULL AND nr_seq_motivo_cancel_w::text <> '') then
		ds_observacao_w := substr(ds_observacao_w || ds_obs_estorno_w,1,4000);

		update	cot_compra
		set 	nr_seq_motivo_cancel 	 = NULL,
			ds_observacao 		= ds_observacao_w,
			nm_usuario		= nm_usuario_p
		where	nr_cot_compra 		= nr_cot_compra_w;

		CALL gerar_historico_cotacao(nr_cot_compra_w,wheb_mensagem_pck.get_texto(300299),wheb_mensagem_pck.get_texto(300301),'S',nm_usuario_p);
	end if;

	select	coalesce(max(nr_cot_compra),0),
		coalesce(max(nr_item_cot_compra),0),
		coalesce(max(nr_solic_compra),0),
		coalesce(max(nr_item_solic_compra),0)
	into STRICT	nr_cot_compra_w,
		nr_item_cot_compra_w,
		nr_solic_compra_w,
		nr_item_solic_compra_w
	from	ordem_compra_item
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci = nr_item_oci_p;

	if	((nr_solic_compra_w = 0) or (nr_item_solic_compra_w = 0)) and (nr_cot_compra_w > 0) and (nr_item_cot_compra_w > 0) then

		select	coalesce(max(nr_solic_compra),0),
			coalesce(max(nr_item_solic_compra),0)
		into STRICT	nr_solic_compra_w,
			nr_item_solic_compra_w
		from	cot_compra_item
		where	nr_cot_compra = nr_cot_compra_w
		and	nr_item_cot_compra = nr_item_cot_compra_w;
	end if;

	if (nr_solic_compra_w > 0) and (nr_item_solic_compra_w > 0) then

		select	cd_motivo_baixa
		into STRICT	cd_motivo_baixa_w
		from	solic_compra_item
		where	nr_solic_compra = nr_solic_compra_w
		and	nr_item_solic_compra = nr_item_solic_compra_w;

		if (cd_motivo_baixa_w > 0) then
			select	ds_observacao,
				coalesce(qt_material_cancelado,0)
			into STRICT	ds_observacao_orig_w,
				qt_material_cancelado_w
			from	solic_compra_item
			where	nr_solic_compra = nr_solic_compra_w
			and	nr_item_solic_compra = nr_item_solic_compra_w;

			if (ds_observacao_orig_w IS NOT NULL AND ds_observacao_orig_w::text <> '') then
				ds_observacao_w	:= substr(wheb_mensagem_pck.get_texto(795794, 'DS_OBSERVACAO_ORIG_W='|| ds_observacao_orig_w||';DS_OBSERVACAO_W='||ds_observacao_w),1,255);
			end if;

			update	solic_compra
			set	cd_motivo_baixa		 = NULL,
				dt_baixa			 = NULL,
				nm_usuario		= nm_usuario_p
			where	nr_solic_compra		= nr_solic_compra_w;

			if ((qt_material_cancelado_w - qt_material_cancelado_p) > 0) then
				update	solic_compra_item
				set	nm_usuario		= nm_usuario_p,
					qt_material_cancelado	= qt_material_cancelado_w - qt_material_cancelado_p
				where	nr_solic_compra		= nr_solic_compra_w
				and	nr_item_solic_compra 	= nr_item_solic_compra_w;
			else
				update	solic_compra_item
				set	cd_motivo_baixa		 = NULL,
					dt_baixa			 = NULL,
					ds_observacao		= ds_observacao_w,
					nm_usuario		= nm_usuario_p,
					qt_material_cancelado	= 0
				where	nr_solic_compra		= nr_solic_compra_w
				and	nr_item_solic_compra 	= nr_item_solic_compra_w;
			end if;

			CALL gerar_historico_solic_compra(
				nr_solic_compra_w,
				wheb_mensagem_pck.get_texto(300302),
				wheb_mensagem_pck.get_texto(300303, 'NR_ITEM_SOLIC_COMPRA=' || nr_item_solic_compra_w),
				'T',
				nm_usuario_p);

		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
